00122     private MenuRecord GetMenuRecord()
00123     {
00124         var renderBatch = TextEditorViewModelDisplay._storedRenderBatchTuple.Validated;
00125         if (renderBatch is null)
00126             return NoResultsMenuRecord;
00127     
00128         try
00129         {
00130             var cursorList = new TextEditorCursor[] { renderBatch.ViewModel.PrimaryCursor }.ToImmutableArray();
00131 
00132             var primaryCursor = cursorList.First(x => x.IsPrimaryCursor);
00133 
00134             if (primaryCursor.ColumnIndex > 0)
00135             {
00136                 var word = renderBatch.Model.ReadPreviousWordOrDefault(
00137                     primaryCursor.LineIndex,
00138                     primaryCursor.ColumnIndex);
00139 
00140                 List<MenuOptionRecord> menuOptionRecordsList = new();
00141 
00142                 if (word is not null)
00143                 {
00144                     var autocompleteWordsList = AutocompleteService.GetAutocompleteOptions(word);
00145 
00146                     var autocompleteEntryList = autocompleteWordsList
00147                         .Select(aw => new AutocompleteEntry(aw, AutocompleteEntryKind.Word, null))
00148                         .ToArray();
00149 
00150                     // (2023-08-09) Looking into using an ICompilerService for autocompletion.
00151                     {
00152                         var positionIndex = renderBatch.Model.GetPositionIndex(primaryCursor);
00153 
00154                         var textSpan = new TextEditorTextSpan(
00155                             positionIndex,
00156                             positionIndex + 1,
00157                             0,
00158                             renderBatch.Model.ResourceUri,
00159                             // TODO: renderBatch.Model.GetAllText() probably isn't needed here. Maybe a useful optimization is to remove it somehow?
00160                             renderBatch.Model.GetAllText());
00161 
00162                         var compilerServiceAutocompleteEntryList = renderBatch.Model.CompilerService.GetAutocompleteEntries(
00163                             word,
00164                             textSpan);
00165 
00166                         if (compilerServiceAutocompleteEntryList.Any())
00167                         {
00168                             autocompleteEntryList = compilerServiceAutocompleteEntryList
00169                                 .AddRange(autocompleteEntryList)
00170                                 .ToArray();
00171                         }
00172                     }
00173 
00174                     menuOptionRecordsList = autocompleteEntryList.Select(entry => new MenuOptionRecord(
00175                         entry.DisplayName,
00176                         MenuOptionKind.Other,
00177                         () => SelectMenuOption(() =>
00178                         {
00179                             if (entry.AutocompleteEntryKind != AutocompleteEntryKind.Snippet)
00180                                 InsertAutocompleteMenuOption(word, entry, renderBatch.ViewModel);
00181                                 
00182                             return entry.SideEffectFunc?.Invoke();
00183                         }),
00184                         WidgetParameterMap: new Dictionary<string, object?>
00185                         {
00186                             {
00187                                 nameof(AutocompleteEntry),
00188                                 entry
00189                             }
00190                         }))
00191                     .ToList();
00192                 }
00193 
00194                 if (!menuOptionRecordsList.Any())
00195                     menuOptionRecordsList.Add(new MenuOptionRecord("No results", MenuOptionKind.Other));
00196 
00197                 return new MenuRecord(menuOptionRecordsList.ToImmutableArray());
00198             }
00199 
00200             return NoResultsMenuRecord;
00201         }
00202         // Catching 'InvalidOperationException' is for the currently occurring case: "Collection was modified; enumeration operation may not execute."
00203         catch (Exception e) when (e is LuthetusTextEditorException || e is InvalidOperationException)
00204         {
00205             return NoResultsMenuRecord;
00206         }
00207     }
