00248     private Task InsertAutocompleteMenuOption(
00249         string word,
00250         AutocompleteEntry autocompleteEntry,
00251         TextEditorViewModel viewModel)
00252     {
00253         var renderBatch = TextEditorViewModelDisplay._storedRenderBatchTuple.Validated;
00254         if (renderBatch is null)
00255             return Task.CompletedTask;
00256     
00257         TextEditorService.PostUnique(
00258             nameof(InsertAutocompleteMenuOption),
00259             editContext =>
00260             {
00261                 var modelModifier = editContext.GetModelModifier(viewModel.ResourceUri);
00262                 var viewModelModifier = editContext.GetViewModelModifier(viewModel.ViewModelKey);
00263                 var cursorModifierBag = editContext.GetCursorModifierBag(viewModelModifier?.ViewModel);
00264                 var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00265 
00266                 if (modelModifier is null || viewModelModifier is null || cursorModifierBag is null || primaryCursorModifier is null)
00267                     return Task.CompletedTask;
00268             
00269                 TextEditorService.ModelApi.InsertText(
00270                     editContext,
00271                     modelModifier,
00272                     cursorModifierBag,
00273                     autocompleteEntry.DisplayName.Substring(word.Length),
00274                     CancellationToken.None);
00275                     
00276                 return renderBatch.ViewModel.FocusAsync();
00277             });
00278         
00279         return renderBatch.ViewModel.FocusAsync();
