00060     public IBinderSession StartBinderSession(ResourceUri resourceUri)
00061     {
00062         foreach (var namespaceGroupNodeKvp in _namespaceGroupNodeMap)
00063         {
00064             var keepStatements = namespaceGroupNodeKvp.Value.NamespaceStatementNodeList
00065                 .Where(x => x.IdentifierToken.TextSpan.ResourceUri != resourceUri)
00066                 .ToImmutableArray();
00067 
00068             _namespaceGroupNodeMap[namespaceGroupNodeKvp.Key] =
00069                 new NamespaceGroupNode(
00070                     namespaceGroupNodeKvp.Value.NamespaceString,
00071                     keepStatements);
00072         }
00073 
00074         foreach (var symbolDefinition in _symbolDefinitions)
00075         {
00076             var keep = symbolDefinition.Value.SymbolReferences
00077                 .Where(x => x.Symbol.TextSpan.ResourceUri != resourceUri)
00078                 .ToList();
00079 
00080             _symbolDefinitions[symbolDefinition.Key] =
00081                 symbolDefinition.Value with
00082                 {
00083                     SymbolReferences = keep
00084                 };
00085         }
00086         
00087         // TODO: Why '_globalScope.IndexKey'? Shouldn't this just be 0, its starting the counter fresh?
00088         var cSharpBinderSession = new CSharpBinderSession(
00089             resourceUri,
00090             this,
00091             0,
00092             _topLevelNamespaceStatementNode);
00093             
00094         cSharpBinderSession.ScopeList.Add(_globalScope);
00095         
00096         return cSharpBinderSession;
00097     }
