00105     public LiteralExpressionNode BindLiteralExpressionNode(
00106         LiteralExpressionNode literalExpressionNode,
00107         CSharpParserModel model)
00108     {
00109         TypeClauseNode typeClauseNode;
00110     
00111         switch (literalExpressionNode.LiteralSyntaxToken.SyntaxKind)
00112         {
00113             case SyntaxKind.NumericLiteralToken:
00114                 typeClauseNode = CSharpFacts.Types.Int.ToTypeClause();
00115                 break;
00116             case SyntaxKind.CharLiteralToken:
00117                 typeClauseNode = CSharpFacts.Types.Char.ToTypeClause();
00118                 break;
00119             case SyntaxKind.StringLiteralToken:
00120                 typeClauseNode = CSharpFacts.Types.String.ToTypeClause();
00121                 break;
00122             default:
00123                 typeClauseNode = CSharpFacts.Types.Void.ToTypeClause();
00124                 model.DiagnosticBag.ReportTodoException(literalExpressionNode.LiteralSyntaxToken.TextSpan, $"{nameof(BindLiteralExpressionNode)}(...) failed to map SyntaxKind: '{literalExpressionNode.LiteralSyntaxToken.SyntaxKind}'");
00125                 break;
00126         }
00127 
00128         return new LiteralExpressionNode(
00129             literalExpressionNode.LiteralSyntaxToken,
00130             typeClauseNode);
00131     }
