00133     public BinaryOperatorNode BindBinaryOperatorNode(
00134         IExpressionNode leftExpressionNode,
00135         ISyntaxToken operatorToken,
00136         IExpressionNode rightExpressionNode,
00137         CSharpParserModel parserModel)
00138     {
00139         var problematicTextSpan = (TextEditorTextSpan?)null;
00140 
00141         if (leftExpressionNode.ResultTypeClauseNode.ValueType == typeof(int))
00142         {
00143             if (rightExpressionNode.ResultTypeClauseNode.ValueType == typeof(int))
00144             {
00145                 switch (operatorToken.SyntaxKind)
00146                 {
00147                     case SyntaxKind.PlusToken:
00148                     case SyntaxKind.MinusToken:
00149                     case SyntaxKind.StarToken:
00150                     case SyntaxKind.DivisionToken:
00151                         return new BinaryOperatorNode(
00152                             leftExpressionNode.ResultTypeClauseNode,
00153                             operatorToken,
00154                             rightExpressionNode.ResultTypeClauseNode,
00155                             CSharpFacts.Types.Int.ToTypeClause());
00156                 }
00157             }
00158             else
00159             {
00160                 problematicTextSpan = rightExpressionNode.ConstructTextSpanRecursively();
00161             }
00162         }
00163         else if (leftExpressionNode.ResultTypeClauseNode.ValueType == typeof(string))
00164         {
00165             if (rightExpressionNode.ResultTypeClauseNode.ValueType == typeof(string))
00166             {
00167                 switch (operatorToken.SyntaxKind)
00168                 {
00169                     case SyntaxKind.PlusToken:
00170                         return new BinaryOperatorNode(
00171                             leftExpressionNode.ResultTypeClauseNode,
00172                             operatorToken,
00173                             rightExpressionNode.ResultTypeClauseNode,
00174                             CSharpFacts.Types.String.ToTypeClause());
00175                 }
00176             }
00177             else
00178             {
00179                 problematicTextSpan = rightExpressionNode.ConstructTextSpanRecursively();
00180             }
00181         }
00182         else
00183         {
00184             problematicTextSpan = leftExpressionNode.ConstructTextSpanRecursively();
00185         }
00186 
00187         if (problematicTextSpan is not null)
00188         {
00189             var errorMessage = $"Operator: {operatorToken.TextSpan.GetText()} is not defined" +
00190                 $" for types: {leftExpressionNode.ConstructTextSpanRecursively().GetText()}" +
00191                 $" and {rightExpressionNode.ConstructTextSpanRecursively().GetText()}";
00192 
00193             parserModel.DiagnosticBag.ReportTodoException(problematicTextSpan.Value, errorMessage);
00194         }
00195 
00196         return new BinaryOperatorNode(
00197             leftExpressionNode.ResultTypeClauseNode,
00198             operatorToken,
00199             rightExpressionNode.ResultTypeClauseNode,
00200             CSharpFacts.Types.Void.ToTypeClause());
00201     }
