00665     public void CloseScope(
00666         TextEditorTextSpan textSpan,
00667         CSharpParserModel model)
00668     {
00669         // Check if it is the global scope, if so return early.
00670         {
00671             if (model.BinderSession.CurrentScopeIndexKey == 0)
00672                 return;
00673         }
00674         
00675         var inBuilder = model.CurrentCodeBlockBuilder;
00676         var inOwner = inBuilder.CodeBlockOwner;
00677         
00678         var outBuilder = model.CurrentCodeBlockBuilder.Parent;
00679         var outOwner = outBuilder?.CodeBlockOwner;
00680         
00681         // Update Scope
00682         {
00683             var scope = model.BinderSession.ScopeList[model.BinderSession.CurrentScopeIndexKey];
00684             scope.EndingIndexExclusive = textSpan.EndingIndexExclusive;
00685             model.BinderSession.ScopeList[model.BinderSession.CurrentScopeIndexKey] = scope;
00686             
00687             // Restore Parent Scope
00688             if (scope.ParentIndexKey is not null)
00689             {
00690                 model.BinderSession.CurrentScopeIndexKey = scope.ParentIndexKey.Value;
00691             }
00692         }
00693         
00694         // Update CodeBlockOwner
00695         if (inOwner is not null)
00696         {
00697             inOwner.SetCodeBlockNode(inBuilder.Build(), model);
00698             
00699             if (inOwner.SyntaxKind == SyntaxKind.NamespaceStatementNode)
00700                 model.Binder.BindNamespaceStatementNode((NamespaceStatementNode)inOwner, model);
00701             else if (inOwner.SyntaxKind == SyntaxKind.TypeDefinitionNode)
00702                 model.Binder.BindTypeDefinitionNode((TypeDefinitionNode)inOwner, model, true);
00703             
00704             // Restore Parent CodeBlockBuilder
00705             if (outBuilder is not null)
00706             {
00707                 model.CurrentCodeBlockBuilder = outBuilder;
00708                 outBuilder.InnerPendingCodeBlockOwner = null;
00709                 
00710                 if (inOwner.SyntaxKind != SyntaxKind.TryStatementTryNode &&
00711                     inOwner.SyntaxKind != SyntaxKind.TryStatementCatchNode &&
00712                     inOwner.SyntaxKind != SyntaxKind.TryStatementFinallyNode)
00713                 {
00714                     outBuilder.ChildList.Add(inOwner);
00715                 }
00716             }
00717         }
00718     }
00719 
00720     public void BindTypeDefinitionNode(
00721         TypeDefinitionNode typeDefinitionNode,
00722         CSharpParserModel model,
00723         bool shouldOverwrite = false)
00724     {
00725         var typeIdentifierText = typeDefinitionNode.TypeIdentifierToken.TextSpan.GetText();
00726         var currentNamespaceStatementText = model.BinderSession.CurrentNamespaceStatementNode.IdentifierToken.TextSpan.GetText();
00727         var namespaceAndTypeIdentifiers = new NamespaceAndTypeIdentifiers(currentNamespaceStatementText, typeIdentifierText);
00728 
00729         typeDefinitionNode.EncompassingNamespaceIdentifierString = currentNamespaceStatementText;
00730         
00731         if (TryGetTypeDefinitionNodeByScope(
00732                 model,
00733                 model.BinderSession.ResourceUri,
00734                 model.BinderSession.CurrentScopeIndexKey,
00735                 typeIdentifierText,
00736                 out var existingTypeDefinitionNode))
00737         {
00738             if (shouldOverwrite || existingTypeDefinitionNode.IsFabricated)
00739             {
00740                 SetTypeDefinitionNodeByScope(
00741                     model,
00742                     model.BinderSession.ResourceUri,
00743                     model.BinderSession.CurrentScopeIndexKey,
00744                     typeIdentifierText,
00745                     typeDefinitionNode);
00746             }
00747         }
00748         else
00749         {
00750             _ = TryAddTypeDefinitionNodeByScope(
00751                 model,
00752                 model.BinderSession.ResourceUri,
00753                 model.BinderSession.CurrentScopeIndexKey,
00754                 typeIdentifierText,
00755                 typeDefinitionNode);
00756         }
00757 
00758         var success = _allTypeDefinitions.TryAdd(namespaceAndTypeIdentifiers, typeDefinitionNode);
00759         if (!success)
00760         {
00761             var entryFromAllTypeDefinitions = _allTypeDefinitions[namespaceAndTypeIdentifiers];
00762             
00763             if (shouldOverwrite || entryFromAllTypeDefinitions.IsFabricated)
00764                 _allTypeDefinitions[namespaceAndTypeIdentifiers] = typeDefinitionNode;
00765         }
00766     }
00767 
00769     private void AddSymbolDefinition(
00770         ISymbol symbol,
00771         CSharpParserModel model)
00772     {
00773         var symbolDefinitionId = ISymbol.GetSymbolDefinitionId(
00774             symbol.TextSpan.GetText(),
00775             model.BinderSession.CurrentScopeIndexKey);
00776 
00777         var symbolDefinition = new SymbolDefinition(
00778             model.BinderSession.CurrentScopeIndexKey,
00779             symbol);
00780 
00781         if (!_symbolDefinitions.TryAdd(
00782                 symbolDefinitionId,
00783                 symbolDefinition))
00784         {
00785             var existingSymbolDefinition = _symbolDefinitions[symbolDefinitionId];
00786 
00787             if (existingSymbolDefinition.IsFabricated)
00788             {
00789                 _symbolDefinitions[symbolDefinitionId] = existingSymbolDefinition with
00790                 {
00791                     IsFabricated = false
00792                 };
00793             }
00794             // TODO: The else branch of this if statement would mean the Symbol definition was found twice, should a diagnostic be reported here?
00795         }
00796 
00797         AddSymbolReference(symbol, model);
00798     }
00799 
00800     private void AddSymbolReference(ISymbol symbol, CSharpParserModel model)
00801     {
00802         var symbolDefinitionId = ISymbol.GetSymbolDefinitionId(
00803             symbol.TextSpan.GetText(),
00804             model.BinderSession.CurrentScopeIndexKey);
00805 
00806         if (!_symbolDefinitions.TryGetValue(
00807                 symbolDefinitionId,
00808                 out var symbolDefinition))
00809         {
00810             symbolDefinition = new SymbolDefinition(
00811                 model.BinderSession.CurrentScopeIndexKey,
00812                 symbol)
00813             {
00814                 IsFabricated = true
00815             };
00816 
00817             // TODO: Symbol definition was not found, should a diagnostic be reported here?
00818             var success = _symbolDefinitions.TryAdd(
00819                 symbolDefinitionId,
00820                 symbolDefinition);
00821 
00822             if (!success)
00823                 _symbolDefinitions[symbolDefinitionId] = symbolDefinition;
00824         }
00825 
00826         symbolDefinition.SymbolReferences.Add(new SymbolReference(
00827             symbol,
00828             model.BinderSession.CurrentScopeIndexKey));
00829     }
00830 
00831     public void CreateVariableSymbol(
00832         IdentifierToken identifierToken,
00833         VariableKind variableKind,
00834         CSharpParserModel model)
00835     {
00836         switch (variableKind)
00837         {
00838             case VariableKind.Field:
00839                 AddSymbolDefinition(new FieldSymbol(identifierToken.TextSpan with
00840                 {
00841                     DecorationByte = (byte)GenericDecorationKind.Field
00842                 }), model);
00843                 break;
00844             case VariableKind.Property:
00845                 AddSymbolDefinition(new PropertySymbol(identifierToken.TextSpan with
00846                 {
00847                     DecorationByte = (byte)GenericDecorationKind.Property
00848                 }), model);
00849                 break;
00850             case VariableKind.Local:
00851                 goto default;
00852             case VariableKind.Closure:
00853                 goto default;
00854             default:
00855                 AddSymbolDefinition(new VariableSymbol(identifierToken.TextSpan with
00856                 {
00857                     DecorationByte = (byte)GenericDecorationKind.Variable
00858                 }), model);
00859                 break;
00860         }
00861     }
