00869     public void ClearStateByResourceUri(ResourceUri resourceUri)
00870     {
00871         foreach (var namespaceGroupNodeKvp in _namespaceGroupNodeMap)
00872         {
00873             var keepStatements = namespaceGroupNodeKvp.Value.NamespaceStatementNodeList
00874                 .Where(x => x.IdentifierToken.TextSpan.ResourceUri != resourceUri)
00875                 .ToImmutableArray();
00876 
00877             _namespaceGroupNodeMap[namespaceGroupNodeKvp.Key] =
00878                 new NamespaceGroupNode(
00879                     namespaceGroupNodeKvp.Value.NamespaceString,
00880                     keepStatements);
00881         }
00882 
00883         foreach (var symbolDefinition in _symbolDefinitions)
00884         {
00885             var keep = symbolDefinition.Value.SymbolReferences
00886                 .Where(x => x.Symbol.TextSpan.ResourceUri != resourceUri)
00887                 .ToList();
00888 
00889             _symbolDefinitions[symbolDefinition.Key] =
00890                 symbolDefinition.Value with
00891                 {
00892                     SymbolReferences = keep
00893                 };
00894         }
00895 
00896         _binderSessionMap.Remove(resourceUri);
00897     }
