00904     public bool TryGetFunctionHierarchically(
00905         IParserModel? parserModel,
00906         ResourceUri resourceUri,
00907         int initialScopeIndexKey,
00908         string identifierText,
00909         out FunctionDefinitionNode? functionDefinitionNode)
00910     {
00911         var localScope = GetScopeByScopeIndexKey(parserModel, resourceUri, initialScopeIndexKey);
00912 
00913         while (localScope is not null)
00914         {
00915             if (TryGetFunctionDefinitionNodeByScope(
00916                     parserModel,
00917                     resourceUri,
00918                     localScope.IndexKey,
00919                     identifierText,
00920                     out functionDefinitionNode))
00921             {
00922                 return true;
00923             }
00924 
00925             if (localScope.ParentIndexKey is null)
00926                 localScope = null;
00927             else
00928                 localScope = GetScopeByScopeIndexKey(parserModel, resourceUri, localScope.ParentIndexKey.Value);
00929         }
00930 
00931         functionDefinitionNode = null;
00932         return false;
00933     }
