01008         GetScope(model: null, textSpan);
01009     
01010     public IScope? GetScope(IParserModel? model, TextEditorTextSpan textSpan)
01011     {
01012         return GetScopeByPositionIndex(model, textSpan.ResourceUri, textSpan.StartingIndexInclusive);
01013     }
01014     
01015     IScope? IBinder.GetScopeByPositionIndex(ResourceUri resourceUri, int positionIndex) =>
01016         GetScopeByPositionIndex(model: null, resourceUri, positionIndex);
01017     
01018     public IScope? GetScopeByPositionIndex(IParserModel? model, ResourceUri resourceUri, int positionIndex)
01019     {
01020         var scopeList = new List<IScope>();
01021         
01022         if (TryGetBinderSession(model, resourceUri, out var targetBinderSession))
01023             scopeList.AddRange(targetBinderSession.ScopeList);
01024         if (TryGetBinderSession(model, ResourceUri.Empty, out var globalBinderSession))
01025             scopeList.AddRange(globalBinderSession.ScopeList);
01026         
01027         var possibleScopes = scopeList.Where(x =>
01028         {
01029             return x.StartingIndexInclusive <= positionIndex &&
01030                    // Global Scope awkwardly has a null ending index exclusive (2023-10-15)
01031                    (x.EndingIndexExclusive >= positionIndex || x.EndingIndexExclusive is null);
01032         });
01033 
01034         return possibleScopes.MinBy(x => positionIndex - x.StartingIndexInclusive);
01035     }
