01067     bool IBinder.TryGetBinderSession(ResourceUri resourceUri, out IBinderSession binderSession) =>
01068         TryGetBinderSession(parserModel: null, resourceUri, out binderSession);
01069     
01076     public bool TryGetBinderSession(IParserModel? parserModel, ResourceUri resourceUri, out IBinderSession binderSession)
01077     {
01078         if (parserModel is not null &&
01079             resourceUri == parserModel.BinderSession.ResourceUri)
01080         {
01081             binderSession = parserModel.BinderSession;
01082             return true;
01083         }
01084         
01085         return _binderSessionMap.TryGetValue(resourceUri, out binderSession);
01086     }
