01088     public void UpsertBinderSession(IBinderSession binderSession)
01089     {
01090         try
01091         {
01092             if (_binderSessionMap.ContainsKey(binderSession.ResourceUri))
01093                 _binderSessionMap[binderSession.ResourceUri] = binderSession;
01094             else
01095                 _binderSessionMap.Add(binderSession.ResourceUri, binderSession);
01096         }
01097         catch (Exception e)
01098         {
01099             Console.WriteLine(e);
01100         }
01101     }
