01387     
01388     public bool TryAddReturnTypeClauseNodeByScope(
01389         IParserModel? model,
01390         ResourceUri resourceUri,
01391         int scopeIndexKey,
01392         TypeClauseNode typeClauseNode)
01393     {       
01394         if (!TryGetBinderSession(model, resourceUri, out var binderSession))
01395         {
01396             typeClauseNode = null;
01397             return false;
01398         }
01399             
01400         return binderSession.ScopeReturnTypeClauseNodeMap.TryAdd(scopeIndexKey, typeClauseNode);
01401     }
