01403     TextEditorTextSpan? IBinder.GetDefinition(TextEditorTextSpan textSpan, ICompilerServiceResource compilerServiceResource) =>
01404         GetDefinition(model: null, textSpan, compilerServiceResource);
01405     
01406     public TextEditorTextSpan? GetDefinition(IParserModel? model, TextEditorTextSpan textSpan, ICompilerServiceResource compilerServiceResource)
01407     {
01408         var boundScope = GetScope(model, textSpan);
01409         
01410         if (compilerServiceResource.CompilationUnit is null)
01411             return null;
01412         
01413         // Try to find a symbol at that cursor position.
01414         var symbols = compilerServiceResource.GetSymbols();
01415         var foundSymbol = (ITextEditorSymbol?)null;
01416         
01417         foreach (var symbol in symbols)
01418         {
01419             if (textSpan.StartingIndexInclusive >= symbol.TextSpan.StartingIndexInclusive &&
01420                 textSpan.StartingIndexInclusive < symbol.TextSpan.EndingIndexExclusive)
01421             {
01422                 foundSymbol = symbol;
01423                 break;
01424             }
01425         }
01426         
01427         if (foundSymbol is null)
01428             return null;
01429             
01430         var currentSyntaxKind = foundSymbol.SyntaxKind;
01431         
01432         switch (currentSyntaxKind)
01433         {
01434             case SyntaxKind.VariableAssignmentExpressionNode:
01435             case SyntaxKind.VariableDeclarationNode:
01436             case SyntaxKind.VariableReferenceNode:
01437             case SyntaxKind.VariableSymbol:
01438             case SyntaxKind.PropertySymbol:
01439             case SyntaxKind.FieldSymbol:
01440             {
01441                 if (TryGetVariableDeclarationHierarchically(
01442                         model,
01443                         textSpan.ResourceUri,
01444                         boundScope.IndexKey,
01445                         textSpan.GetText(),
01446                         out var variableDeclarationStatementNode)
01447                     && variableDeclarationStatementNode is not null)
01448                 {
01449                     return variableDeclarationStatementNode.IdentifierToken.TextSpan;
01450                 }
01451                 
01452                 return null;
01453             }
01454             case SyntaxKind.FunctionInvocationNode:
01455             case SyntaxKind.FunctionDefinitionNode:
01456             case SyntaxKind.FunctionSymbol:
01457             {
01458                 if (TryGetFunctionHierarchically(
01459                              model,
01460                              textSpan.ResourceUri,
01461                              boundScope.IndexKey,
01462                              textSpan.GetText(),
01463                              out var functionDefinitionNode)
01464                          && functionDefinitionNode is not null)
01465                 {
01466                     return functionDefinitionNode.FunctionIdentifierToken.TextSpan;
01467                 }
01468                 
01469                 return null;
01470             }
01471             case SyntaxKind.TypeClauseNode:
01472             case SyntaxKind.TypeDefinitionNode:
01473             case SyntaxKind.TypeSymbol:
01474             case SyntaxKind.ConstructorSymbol:
01475             {
01476                 if (TryGetTypeDefinitionHierarchically(
01477                              model,
01478                              textSpan.ResourceUri,
01479                              boundScope.IndexKey,
01480                              textSpan.GetText(),
01481                              out var typeDefinitionNode)
01482                          && typeDefinitionNode is not null)
01483                 {
01484                     return typeDefinitionNode.TypeIdentifierToken.TextSpan;
01485                 }
01486                 
01487                 return null;
01488             }
01489         }
01490 
01491         return null;
01492     }
01493 
01494     public ISyntaxNode? GetSyntaxNode(int positionIndex, CompilationUnit compilationUnit)
01495     {
01496         // First attempt at writing this, will be to start from the root of the compilation unit,
01497         // then traverse the syntax tree where the position index is within bounds.
01498 
01499         return RecursiveGetSyntaxNode(positionIndex, compilationUnit.RootCodeBlockNode);
01500 
01501         ISyntaxNode? RecursiveGetSyntaxNode(int positionIndex, ISyntaxNode targetNode)
01502         {
01503             foreach (var child in targetNode.GetChildList())
01504             {
01505                 if (child is ISyntaxNode syntaxNode)
01506                 {
01507                     var innerResult = RecursiveGetSyntaxNode(positionIndex, syntaxNode);
01508 
01509                     if (innerResult is not null)
01510                         return innerResult;
01511                 }
01512                 else if (child is ISyntaxToken syntaxToken)
01513                 {
01514                     if (syntaxToken.TextSpan.StartingIndexInclusive <= positionIndex &&
01515                         syntaxToken.TextSpan.EndingIndexExclusive >= positionIndex)
01516                     {
01517                         return targetNode;
01518                     }
01519                 }
01520             }
01521 
01522             return null;
01523         }
01524     }
01525 }
