00184     private void AddSnippets(List<AutocompleteEntry> autocompleteEntryList, string word, TextEditorTextSpan textSpan)
00185     {
00186         if ("prop".Contains(word))
00187         {
00188             autocompleteEntryList.Add(new AutocompleteEntry(
00189                 "prop",
00190                 AutocompleteEntryKind.Snippet,
00191                 () => PropSnippet(word, textSpan, "public TYPE NAME { get; set; }")));
00192         }
00193         
00194         if ("propnn".Contains(word))
00195         {
00196             autocompleteEntryList.Add(new AutocompleteEntry(
00197                 "propnn",
00198                 AutocompleteEntryKind.Snippet,
00199                 () => PropSnippet(word, textSpan, "public TYPE NAME { get; set; } = null!;")));
00200         }
00201     }
