00203     private Task PropSnippet(string word, TextEditorTextSpan textSpan, string textToInsert)
00204     {
00205         _textEditorService.PostUnique(
00206             nameof(PropSnippet),
00207             editContext =>
00208             {
00209                 var modelModifier = editContext.GetModelModifier(textSpan.ResourceUri);
00210     
00211                 if (modelModifier is null)
00212                     return Task.CompletedTask;
00213     
00214                 var viewModelList = _textEditorService.ModelApi.GetViewModelsOrEmpty(textSpan.ResourceUri);
00215                 
00216                 var viewModel = viewModelList.FirstOrDefault(x => x.Category.Value == "main")
00217                     ?? viewModelList.FirstOrDefault();
00218                 
00219                 if (viewModel is null)
00220                     return Task.CompletedTask;
00221                     
00222                 var viewModelModifier = editContext.GetViewModelModifier(viewModel.ViewModelKey);
00223 
00224                 var primaryCursorModifier = editContext.GetPrimaryCursorModifier(
00225                     editContext.GetCursorModifierBag(viewModelModifier?.ViewModel));
00226                 
00227                 if (viewModelModifier is null || primaryCursorModifier is null)
00228                     return Task.CompletedTask;
00229     
00230                 var cursorModifierBag = new CursorModifierBagTextEditor(
00231                     Key<TextEditorViewModel>.Empty,
00232                     new List<TextEditorCursorModifier> { primaryCursorModifier });
00233                     
00234                 var cursorPositionIndex = modelModifier.GetPositionIndex(primaryCursorModifier);
00235                 var behindPositionIndex = cursorPositionIndex - 1;
00236                 
00237                 var behindCursor = new TextEditorCursor(
00238                     primaryCursorModifier.LineIndex,
00239                     primaryCursorModifier.ColumnIndex - 1,
00240                     primaryCursorModifier.IsPrimaryCursor);
00241                         
00242                 modelModifier.Insert(
00243                     textToInsert,
00244                     cursorModifierBag,
00245                     cancellationToken: CancellationToken.None);
00246                     
00247                 if (behindPositionIndex > 0 && modelModifier.GetCharacter(behindPositionIndex) == 'p')
00248                 {
00249                     modelModifier.Delete(
00250                         new CursorModifierBagTextEditor(
00251                             Key<TextEditorViewModel>.Empty,
00252                             new List<TextEditorCursorModifier> { new(behindCursor) }),
00253                         1,
00254                         expandWord: false,
00255                         TextEditorModelModifier.DeleteKind.Delete);
00256                 }
00257     
00258                 modelModifier.CompilerService.ResourceWasModified(
00259                     modelModifier.ResourceUri,
00260                     ImmutableArray<TextEditorTextSpan>.Empty);
00261                     
00262                 return Task.CompletedTask;
00263             });
00264             
00265         return Task.CompletedTask;
00266     }
