00045     public CompilationUnit Parse()
00046     {
00047         var globalCodeBlockBuilder = new CodeBlockBuilder(null, null);
00048         var currentCodeBlockBuilder = globalCodeBlockBuilder;
00049         var diagnosticBag = new DiagnosticBag();
00050 
00051         var model = new CSharpParserModel(
00052             Binder,
00053             BinderSession,
00054             new TokenWalker(Lexer.SyntaxTokenList, diagnosticBag),
00055             new Stack<ISyntax>(),
00056             diagnosticBag,
00057             globalCodeBlockBuilder,
00058             currentCodeBlockBuilder);
00059             
00060         #if DEBUG
00061         model.TokenWalker.ProtectedTokenSyntaxKindList = new() { SyntaxKind.StatementDelimiterToken, SyntaxKind.OpenBraceToken, SyntaxKind.CloseBraceToken, };
00062         #endif
00063         
00064         while (true)
00065         {
00066             // The last statement in this while loop is conditionally: '_ = model.TokenWalker.Consume();'.
00067             // Knowing this to be the case is extremely important.
00068             var token = model.TokenWalker.Current;
00069             
00070             #if DEBUG
00071             Console.WriteLine(token.SyntaxKind + "___" + token.TextSpan.GetText() + "___" + model.TokenWalker.Index);
00072             #endif
00073 
00074             switch (token.SyntaxKind)
00075             {
00076                 case SyntaxKind.NumericLiteralToken:
00077                 case SyntaxKind.CharLiteralToken:
00078                 case SyntaxKind.StringLiteralToken:
00079                 case SyntaxKind.PlusToken:
00080                 case SyntaxKind.PlusPlusToken:
00081                 case SyntaxKind.MinusToken:
00082                 case SyntaxKind.StarToken:
00083                 case SyntaxKind.DollarSignToken:
00084                 case SyntaxKind.AtToken:
00085                     if (model.StatementBuilder.ChildList.Count == 0)
00086                     {
00087                         ParseOthers.StartStatement_Expression(model);
00088                     }
00089                     else
00090                     {
00091                         var expressionNode = ParseOthers.ParseExpression(model);
00092                         model.StatementBuilder.ChildList.Add(expressionNode);
00093                     }
00094                     break;
00095                 case SyntaxKind.PreprocessorDirectiveToken:
00096                     ParseTokens.ParsePreprocessorDirectiveToken((PreprocessorDirectiveToken)token, model);
00097                     break;
00098                 case SyntaxKind.IdentifierToken:
00099                     ParseTokens.ParseIdentifierToken(model);
00100                     break;
00101                 case SyntaxKind.OpenBraceToken:
00102                     model.StatementBuilder.FinishStatement(model);
00103                     
00104                     #if DEBUG
00105                     model.TokenWalker.SuppressProtectedSyntaxKindConsumption = true;
00106                     #endif
00107                     
00108                     var openBraceToken = (OpenBraceToken)model.TokenWalker.Consume();
00109                     
00110                     #if DEBUG
00111                     model.TokenWalker.SuppressProtectedSyntaxKindConsumption = false;
00112                     #endif
00113                     
00114                     ParseTokens.ParseOpenBraceToken(openBraceToken, model);
00115                     break;
00116                 case SyntaxKind.CloseBraceToken:
00117                     model.StatementBuilder.FinishStatement(model);
00118                     
00119                     #if DEBUG
00120                     model.TokenWalker.SuppressProtectedSyntaxKindConsumption = true;
00121                     #endif
00122                     
00123                     var closeBraceToken = (CloseBraceToken)model.TokenWalker.Consume();
00124                     
00125                     #if DEBUG
00126                     model.TokenWalker.SuppressProtectedSyntaxKindConsumption = false;
00127                     #endif
00128                     
00129                     ParseTokens.ParseCloseBraceToken(closeBraceToken, model);
00130                     break;
00131                 case SyntaxKind.OpenParenthesisToken:
00132                     if (model.StatementBuilder.ChildList.Count == 0)
00133                         ParseOthers.StartStatement_Expression(model);
00134                     else
00135                         ParseTokens.ParseOpenParenthesisToken(model);
00136                     break;
00137                 case SyntaxKind.CloseParenthesisToken:
00138                     ParseTokens.ParseCloseParenthesisToken((CloseParenthesisToken)token, model);
00139                     break;
00140                 case SyntaxKind.OpenAngleBracketToken:
00141                     if (model.StatementBuilder.ChildList.Count == 0)
00142                         ParseOthers.StartStatement_Expression(model);
00143                     else
00144                         ParseTokens.ParseOpenAngleBracketToken((OpenAngleBracketToken)token, model);
00145                     break;
00146                 case SyntaxKind.CloseAngleBracketToken:
00147                     ParseTokens.ParseCloseAngleBracketToken((CloseAngleBracketToken)token, model);
00148                     break;
00149                 case SyntaxKind.OpenSquareBracketToken:
00150                     ParseTokens.ParseOpenSquareBracketToken(model);
00151                     break;
00152                 case SyntaxKind.CloseSquareBracketToken:
00153                     ParseTokens.ParseCloseSquareBracketToken((CloseSquareBracketToken)token, model);
00154                     break;
00155                 case SyntaxKind.ColonToken:
00156                     ParseTokens.ParseColonToken(model);
00157                     break;
00158                 case SyntaxKind.MemberAccessToken:
00159                     ParseTokens.ParseMemberAccessToken((MemberAccessToken)token, model);
00160                     break;
00161                 case SyntaxKind.EqualsToken:
00162                     ParseTokens.ParseEqualsToken(model);
00163                     break;
00164                 case SyntaxKind.StatementDelimiterToken:
00165                     model.StatementBuilder.FinishStatement(model);
00166                     
00167                     #if DEBUG
00168                     model.TokenWalker.SuppressProtectedSyntaxKindConsumption = true;
00169                     #endif
00170                     
00171                     var statementDelimiterToken = (StatementDelimiterToken)model.TokenWalker.Consume();
00172                     
00173                     #if DEBUG
00174                     model.TokenWalker.SuppressProtectedSyntaxKindConsumption = false;
00175                     #endif
00176                     
00177                     ParseTokens.ParseStatementDelimiterToken(statementDelimiterToken, model);
00178                     break;
00179                 case SyntaxKind.EndOfFileToken:
00180                     if (model.SyntaxStack.TryPeek(out var syntax) &&
00181                         syntax is EndOfFileToken)
00182                     {
00183                         _ = model.SyntaxStack.Pop();
00184                     }
00185 
00186                     if (model.SyntaxStack.TryPop(out var notUsedSyntax))
00187                     {
00188                         if (notUsedSyntax is null)
00189                         {
00190                         }
00191                         else if (notUsedSyntax is IExpressionNode)
00192                         {
00193                             model.CurrentCodeBlockBuilder.ChildList.Add(notUsedSyntax);
00194                         }
00195                         else if (notUsedSyntax.SyntaxKind == SyntaxKind.AmbiguousIdentifierNode)
00196                         {
00197                             var ambiguousIdentifierNode = (AmbiguousIdentifierNode)notUsedSyntax;
00198                             model.CurrentCodeBlockBuilder.ChildList.Add(notUsedSyntax);
00199                             model.DiagnosticBag.ReportUndefinedTypeOrNamespace(
00200                                 ambiguousIdentifierNode.IdentifierToken.TextSpan,
00201                                 ambiguousIdentifierNode.IdentifierToken.TextSpan.GetText());
00202                         }
00203                     }
00204                     else if (model.StatementBuilder.ChildList.Any())
00205                     {
00206                         foreach (var item in model.StatementBuilder.ChildList)
00207                         {
00208                             model.CurrentCodeBlockBuilder.ChildList.Add(item);
00209                         }
00210                     }
00211                     break;
00212                 default:
00213                     if (UtilityApi.IsContextualKeywordSyntaxKind(token.SyntaxKind))
00214                         ParseTokens.ParseKeywordContextualToken(model);
00215                     else if (UtilityApi.IsKeywordSyntaxKind(token.SyntaxKind))
00216                         ParseTokens.ParseKeywordToken(model);
00217                     break;
00218             }
00219 
00220             if (token.SyntaxKind == SyntaxKind.EndOfFileToken)
00221             {
00222                 if (model.CurrentCodeBlockBuilder.ParseChildScopeQueue.TryDequeue(out var deferredChildScope))
00223                     deferredChildScope.PrepareMainParserLoop(model.TokenWalker.Index, model);
00224                 else
00225                     break;
00226             }
00227             
00228             if (model.TokenWalker.ConsumeCounter == 0)
00229             {
00230                 // This means either:
00231                 //  - None of the methods for syntax could make sense of the token, so they didn't consume it.
00232                 //  - For whatever reason the method that handled the syntax made sense of the token, but never consumed it.
00233                 //  - The token was consumed, then for some reason a backtrack occurred.
00234                 //
00235                 // To avoid an infinite loop, this will ensure at least 1 token is consumed each iteration of the while loop.
00236                 // 
00237                 // (and that the token index increased by at least 1 from the previous loop; this is implicitly what is implied).
00238                 _ = model.TokenWalker.Consume();
00239             }
00240             else if (model.TokenWalker.ConsumeCounter < 0)
00241             {
00242                 // This means that a syntax invoked 'model.TokenWalker.Backtrack()'.
00243                 // Without invoking an equal amount of 'model.TokenWalker.Consume()' to avoid an infinite loop.
00244                 throw new LuthetusTextEditorException($"model.TokenWalker.ConsumeCounter:{model.TokenWalker.ConsumeCounter} < 0");
00245             }
00246             
00247             model.TokenWalker.ConsumeCounterReset();
00248         }
00249 
00250         if (model.CurrentCodeBlockBuilder.Parent is not null)
00251         {
00252             // The current token here would be the EOF token.
00253             Binder.CloseScope(model.TokenWalker.Current.TextSpan, model);
00254         }
00255 
00256         DiagnosticsList = DiagnosticsList.AddRange(model.DiagnosticBag.ToImmutableArray());
00257 
00258         var topLevelStatementsCodeBlock = model.CurrentCodeBlockBuilder.Build(
00259             DiagnosticsList
00260                 .Union(Binder.DiagnosticsList)
00261                 .Union(Lexer.DiagnosticList)
00262                 .ToImmutableArray());
00263 
00264         Binder.FinalizeBinderSession(BinderSession);
00265         return new CompilationUnit(
00266             topLevelStatementsCodeBlock,
00267             Lexer,
00268             this,
00269             Binder);
00270     }
00271 }
