00303         // Focus the text editor itself (as to allow for typing into the editor)
00304         {
00305             var focusTextEditorCommand = new CommonCommand(
00306                 "Focus: Text Editor", "focus-text-editor", false,
00307                 async commandArgs =>
00308                 {
00309                     var group = _textEditorService.GroupApi.GetOrDefault(EditorIdeApi.EditorTextEditorGroupKey);
00310 
00311                     if (group is null)
00312                         return;
00313 
00314                     var activeViewModel = _textEditorService.ViewModelApi.GetOrDefault(group.ActiveViewModelKey);
00315 
00316                     if (activeViewModel is null)
00317                         return;
00318 
00319                     await _jsRuntime.GetLuthetusCommonApi()
00320                         .FocusHtmlElementById(activeViewModel.PrimaryCursorContentId)
00321                         .ConfigureAwait(false);
00322                 });
