00388         // Add command to bring up a Context Switch dialog. Example: { Ctrl + Tab }
00389         {
00390             // TODO: determine the actively focused element at time of invocation,
00391             //       then restore focus to that element when this dialog is closed.
00392             var openContextSwitchDialogCommand = new CommonCommand(
00393                 "Open: Context Switch", "open-context-switch", false,
00394                 async commandArgs =>
00395                 {
00396                     var jsRuntimeCommonApi = _jsRuntime.GetLuthetusCommonApi();
00397                     
00398                     var elementDimensions = await jsRuntimeCommonApi
00399                         .MeasureElementById("luth_ide_header-button-file")
00400                         .ConfigureAwait(false);
00401                         
00402                     var contextState = _contextStateWrap.Value;
00403                     
00404                     var menuOptionList = new List<MenuOptionRecord>();
00405                     
00406                     foreach (var context in contextState.AllContextsList)
00407                     {
00408                         menuOptionList.Add(new MenuOptionRecord(
00409                             context.DisplayNameFriendly,
00410                             MenuOptionKind.Other));
00411                     }
00412                     
00413                     MenuRecord menu;
00414                     
00415                     if (menuOptionList.Count == 0)
00416                         menu = MenuRecord.Empty;
00417                     else
00418                         menu = new MenuRecord(menuOptionList.ToImmutableArray());
00419                         
00420                     var dropdownRecord = new DropdownRecord(
00421                         Key<DropdownRecord>.NewKey(),
00422                         elementDimensions.LeftInPixels,
00423                         elementDimensions.TopInPixels + elementDimensions.HeightInPixels,
00424                         typeof(MenuDisplay),
00425                         new Dictionary<string, object?>
00426                         {
00427                             {
00428                                 nameof(MenuDisplay.MenuRecord),
00429                                 menu
00430                             }
00431                         },
00432                         () => Task.CompletedTask);
00433             
00434                     // _dispatcher.Dispatch(new DropdownState.RegisterAction(dropdownRecord));
00435                     
00436                     if (_contextStateWrap.Value.FocusedContextHeirarchy.NearestAncestorKey ==
00437                             ContextFacts.TextEditorContext.ContextKey)
00438                     {
00439                         _contextSwitchStateWrap.Value.FocusInitiallyContextSwitchGroupKey = LuthetusTextEditorInitializer.ContextSwitchGroupKey;
00440                     }
00441                     else
00442                     {
00443                         _contextSwitchStateWrap.Value.FocusInitiallyContextSwitchGroupKey = LuthetusCommonInitializer.ContextSwitchGroupKey;
00444                     }
00445                 
00446                     _contextSwitchWidget ??= new WidgetModel(
00447                         typeof(ContextSwitchDisplay),
00448                         componentParameterMap: null,
00449                         cssClass: null,
00450                         cssStyle: null);
00451 
00452                     _dispatcher.Dispatch(new WidgetState.SetWidgetAction(_contextSwitchWidget));
00453                 });
00454 
