00035     protected override async Task OnAfterRenderAsync(bool firstRender)
00036     {
00037         if (firstRender)
00038         {
00039             if (_textEditorContextMenuElementReference is not null)
00040             {
00041                 try
00042                 {
00043                     await _textEditorContextMenuElementReference.Value
00044                         .FocusAsync(preventScroll: true)
00045                         .ConfigureAwait(false);
00046                 }
00047                 catch (Exception)
00048                 {
00049                     // 2023-04-18: The app has had a bug where it "freezes" and must be restarted.
00050                     //             This bug is seemingly happening randomly. I have a suspicion
00051                     //             that there are race-condition exceptions occurring with "FocusAsync"
00052                     //             on an ElementReference.
00053                 }
00054             }
00055         }
00056 
00057         await base.OnAfterRenderAsync(firstRender);
00058     }
