00073     private void HandleOnKeyDown(KeyboardEventArgs keyboardEventArgs)
00074     {
00075         var renderBatch = TextEditorViewModelDisplay._storedRenderBatchTuple.Validated;
00076         if (renderBatch is null)
00077             return;
00078         
00079         if (KeyboardKeyFacts.MetaKeys.ESCAPE == keyboardEventArgs.Key)
00080         {
00081             TextEditorService.PostUnique(
00082                 nameof(ContextMenu),
00083                 editContext =>
00084                 {
00085                     var viewModelModifier = editContext.GetViewModelModifier(renderBatch.ViewModel.ViewModelKey);
00086 
00087                     if (viewModelModifier.ViewModel.MenuKind != MenuKind.None)
00088                     {
00089                         TextEditorCommandDefaultFunctions.RemoveDropdown(
00090                             editContext,
00091                             viewModelModifier,
00092                             Dispatcher);
00093                     }
00094 
00095                     return Task.CompletedTask;
00096                 });
00097         }
00098     }
