00133     private MenuRecord GetMenuRecord()
00134     {
00135         List<MenuOptionRecord> menuOptionRecordsList = new();
00136 
00137         var cut = new MenuOptionRecord("Cut (Ctrl x)", MenuOptionKind.Other, () => SelectMenuOption(CutMenuOption));
00138         menuOptionRecordsList.Add(cut);
00139 
00140         var copy = new MenuOptionRecord("Copy (Ctrl c)", MenuOptionKind.Other, () => SelectMenuOption(CopyMenuOption));
00141         menuOptionRecordsList.Add(copy);
00142 
00143         var paste = new MenuOptionRecord("Paste (Ctrl v)", MenuOptionKind.Other, () => SelectMenuOption(PasteMenuOption));
00144         menuOptionRecordsList.Add(paste);
00145         
00146         var goToDefinition = new MenuOptionRecord("Go to definition (F12)", MenuOptionKind.Other, () => SelectMenuOption(GoToDefinitionOption));
00147         menuOptionRecordsList.Add(goToDefinition);
00148         
00149         var quickActionsSlashRefactors = new MenuOptionRecord("QuickActions/Refactors (Ctrl .)", MenuOptionKind.Other, () => SelectMenuOption(QuickActionsSlashRefactors));
00150         menuOptionRecordsList.Add(quickActionsSlashRefactors);
00151 
00152         if (!menuOptionRecordsList.Any())
00153             menuOptionRecordsList.Add(new MenuOptionRecord("No Context Menu Options for this item", MenuOptionKind.Other));
00154 
00155         return new MenuRecord(menuOptionRecordsList.ToImmutableArray());
00156     }
