00158     private Task SelectMenuOption(Func<Task> menuOptionAction)
00159     {
00160         var renderBatch = TextEditorViewModelDisplay._storedRenderBatchTuple.Validated;
00161         if (renderBatch is null)
00162             return Task.CompletedTask;
00163             
00164         _ = Task.Run(async () =>
00165         {
00166             try
00167             {
00168                 TextEditorService.PostUnique(
00169                     nameof(ContextMenu),
00170                     editContext =>
00171                     {
00172                         var viewModelModifier = editContext.GetViewModelModifier(renderBatch.ViewModel.ViewModelKey);
00173     
00174                         if (viewModelModifier.ViewModel.MenuKind != MenuKind.None)
00175                         {
00176                             TextEditorCommandDefaultFunctions.RemoveDropdown(
00177                                 editContext,
00178                                 viewModelModifier,
00179                                 Dispatcher);
00180                         }
00181 
00182                         return Task.CompletedTask;
00183                     });
00184 
00185                 await menuOptionAction.Invoke().ConfigureAwait(false);
00186                 TextEditorService.ViewModelApi.SetCursorShouldBlink(false);
00187             }
00188             catch (Exception e)
00189             {
00190                 Console.WriteLine(e);
00191                 throw;
00192             }
00193         }, CancellationToken.None);
00194 
00195         return Task.CompletedTask;
00196     }
