00264             nameof(TextEditorCommandDefaultFacts.GoToDefinition),
00265             editContext =>
00266             {
00267                 commandArgs.EditContext = editContext;
00268                 
00269                 var viewModelModifier = commandArgs.EditContext.GetViewModelModifier(commandArgs.ViewModelKey);
00270                 
00271                 if (viewModelModifier is null)
00272                     return Task.CompletedTask;
00273                 
00274                 viewModelModifier.ViewModel.UnsafeState.ShouldRevealCursor = true;
00275                 
00276                 return TextEditorCommandDefaultFacts.GoToDefinition.CommandFunc
00277                     .Invoke(commandArgs);
00278             });
00279         return Task.CompletedTask;
