00016     public static CssSyntaxUnit ParseText(
00017         ResourceUri resourceUri,
00018         string sourceText)
00019     {
00020         // Items to return wrapped in a CssSyntaxUnit
00021         var cssDocumentChildren = new List<ICssSyntax>();
00022         var textEditorCssDiagnosticBag = new DiagnosticBag();
00023 
00024         // Step through the string 'character by character'
00025         var stringWalker = new StringWalker(resourceUri, sourceText);
00026 
00027         // Order matters with the methods of pattern, 'Consume{Something}'
00028         // Example: 'ConsumeComment'
00029         while (!stringWalker.IsEof)
00030         {
00031             if (char.IsLetterOrDigit(stringWalker.CurrentCharacter))
00032                 ConsumeIdentifier(stringWalker, cssDocumentChildren, textEditorCssDiagnosticBag);
00033 
00034             if (stringWalker.PeekForSubstring(CssFacts.COMMENT_START))
00035                 ConsumeComment(stringWalker, cssDocumentChildren, textEditorCssDiagnosticBag);
00036 
00037             if (stringWalker.CurrentCharacter == CssFacts.STYLE_BLOCK_START)
00038                 ConsumeStyleBlock(stringWalker, cssDocumentChildren, textEditorCssDiagnosticBag);
00039 
00040             _ = stringWalker.ReadCharacter();
00041         }
00042 
00043         var cssDocumentSyntax = new CssDocumentSyntax(
00044             new TextEditorTextSpan(
00045                 0,
00046                 stringWalker.PositionIndex,
00047                 (byte)CssDecorationKind.None,
00048                 stringWalker.ResourceUri,
00049                 stringWalker.SourceText),
00050             cssDocumentChildren.ToImmutableArray());
00051 
00052         var cssSyntaxUnit = new CssSyntaxUnit(
00053             cssDocumentSyntax,
00054             textEditorCssDiagnosticBag);
00055 
00056         return cssSyntaxUnit;
00057     }
00058 
00064     private static void ConsumeComment(
00065         StringWalker stringWalker,
00066         List<ICssSyntax> cssDocumentChildren,
00067         DiagnosticBag diagnosticBag)
00068     {
00069         var commentStartingPositionIndex = stringWalker.PositionIndex;
00070 
00071         while (!stringWalker.IsEof)
00072         {
00073             _ = stringWalker.ReadCharacter();
00074 
00075             var closingOfCommentTextFound = stringWalker
00076                 .PeekForSubstring(CssFacts.COMMENT_END);
00077 
00078             if (closingOfCommentTextFound)
00079             {
00080                 // Skip the rest of the comment closing text
00081                 _ = stringWalker.ReadRange(CssFacts.COMMENT_END.Length - 1);
00082 
00083                 var commentTextSpan = new TextEditorTextSpan(
00084                     commentStartingPositionIndex,
00085                     stringWalker.PositionIndex + 1,
00086                     (byte)CssDecorationKind.Comment,
00087                     stringWalker.ResourceUri,
00088                     stringWalker.SourceText);
00089 
00090                 var commentToken = new CssCommentSyntax(
00091                     commentTextSpan,
00092                     ImmutableArray<ICssSyntax>.Empty);
00093 
00094                 cssDocumentChildren.Add(commentToken);
00095 
00096                 return;
00097             }
00098         }
00099     }
