00106     private static void ConsumeStyleBlock(
00107         StringWalker stringWalker,
00108         List<ICssSyntax> cssDocumentChildren,
00109         DiagnosticBag diagnosticBag)
00110     {
00111         var expectedStyleBlockChild = CssSyntaxKind.PropertyName;
00112 
00113         // when pendingChildStartingPositionIndex == -1 it is to
00114         // mean that there is NOT a pending child
00115         var pendingChildStartingPositionIndex = -1;
00116 
00117         var textOfChildAlreadyFound = false;
00118 
00119         while (!stringWalker.IsEof)
00120         {
00121             _ = stringWalker.ReadCharacter();
00122 
00123             if (pendingChildStartingPositionIndex == -1)
00124             {
00125                 if (stringWalker.CurrentCharacter == CssFacts.STYLE_BLOCK_END)
00126                     break;
00127 
00128                 if (stringWalker.PeekForSubstring(CssFacts.COMMENT_START))
00129                 {
00130                     ConsumeComment(stringWalker, cssDocumentChildren, diagnosticBag);
00131                     continue;
00132                 }
00133             }
00134 
00135             char childEndingCharacter;
00136             CssDecorationKind childDecorationKind;
00137 
00138             switch (expectedStyleBlockChild)
00139             {
00140                 case CssSyntaxKind.PropertyName:
00141                     childEndingCharacter = CssFacts.PROPERTY_NAME_END;
00142                     childDecorationKind = CssDecorationKind.PropertyName;
00143                     break;
00144                 case CssSyntaxKind.PropertyValue:
00145                     childEndingCharacter = CssFacts.PROPERTY_VALUE_END;
00146                     childDecorationKind = CssDecorationKind.PropertyValue;
00147                     break;
00148                 default:
00149                     throw new LuthetusTextEditorException($"The {nameof(CssSyntaxKind)} of" +
00150                                                    $" {expectedStyleBlockChild} was unexpected.");
00151             }
00152 
00153             // Skip preceding and trailing whitespace
00154             // relative to the child's text
00155             if (WhitespaceFacts.ALL_LIST.Contains(stringWalker.CurrentCharacter) &&
00156                 pendingChildStartingPositionIndex == -1)
00157             {
00158                 continue;
00159             }
00160 
00161             // Start of a child's text
00162             if (pendingChildStartingPositionIndex == -1)
00163             {
00164                 pendingChildStartingPositionIndex = stringWalker.PositionIndex;
00165                 continue;
00166             }
00167 
00168             // End of a child's text
00169             if (!textOfChildAlreadyFound &&
00170                 stringWalker.CurrentCharacter == childEndingCharacter)
00171             {
00172                 var childTextSpan = new TextEditorTextSpan(
00173                     pendingChildStartingPositionIndex,
00174                     stringWalker.PositionIndex,
00175                     (byte)childDecorationKind,
00176                     stringWalker.ResourceUri,
00177                     stringWalker.SourceText);
00178 
00179                 ICssSyntax childSyntax;
00180 
00181                 switch (expectedStyleBlockChild)
00182                 {
00183                     case CssSyntaxKind.PropertyName:
00184                         childSyntax = new CssPropertyNameSyntax(
00185                             childTextSpan,
00186                             ImmutableArray<ICssSyntax>.Empty);
00187                         break;
00188                     case CssSyntaxKind.PropertyValue:
00189                         childSyntax = new CssPropertyValueSyntax(
00190                             childTextSpan,
00191                             ImmutableArray<ICssSyntax>.Empty);
00192                         break;
00193                     default:
00194                         throw new LuthetusTextEditorException($"The {nameof(CssSyntaxKind)} of" +
00195                                                        $" {expectedStyleBlockChild} was unexpected.");
00196                 }
00197 
00198                 cssDocumentChildren.Add(childSyntax);
00199 
00200                 textOfChildAlreadyFound = true;
00201             }
00202 
00203             // Clear and ready state for finding the next expected child
00204             if (stringWalker.CurrentCharacter == childEndingCharacter)
00205             {
00206                 pendingChildStartingPositionIndex = -1;
00207                 textOfChildAlreadyFound = false;
00208 
00209                 switch (expectedStyleBlockChild)
00210                 {
00211                     case CssSyntaxKind.PropertyName:
00212                         expectedStyleBlockChild = CssSyntaxKind.PropertyValue;
00213                         break;
00214                     case CssSyntaxKind.PropertyValue:
00215                         expectedStyleBlockChild = CssSyntaxKind.PropertyName;
00216                         break;
00217                     default:
00218                         throw new LuthetusTextEditorException($"The {nameof(CssSyntaxKind)} of" +
00219                                                        $" {expectedStyleBlockChild} was unexpected.");
00220                 }
00221             }
00222 
00223             // Relies on the if statement before this that ensures
00224             // the current character is not whitespace
00225             if (stringWalker.CurrentCharacter != childEndingCharacter)
00226             {
00227                 var unexpectedTokenTextSpan = new TextEditorTextSpan(
00228                     pendingChildStartingPositionIndex,
00229                     stringWalker.PositionIndex,
00230                     (byte)CssDecorationKind.UnexpectedToken,
00231                     stringWalker.ResourceUri,
00232                     stringWalker.SourceText);
00233 
00234                 diagnosticBag.ReportUnexpectedToken(
00235                     unexpectedTokenTextSpan,
00236                     stringWalker.CurrentCharacter.ToString());
00237 
00238                 continue;
00239             }
00240         }
00241     }
