00248     private static void ConsumeIdentifier(
00249         StringWalker stringWalker,
00250         List<ICssSyntax> cssDocumentChildren,
00251         DiagnosticBag diagnosticBag)
00252     {
00253         var startingPositionIndex = stringWalker.PositionIndex;
00254 
00255         while (!stringWalker.IsEof)
00256         {
00257             _ = stringWalker.ReadCharacter();
00258 
00259             if (WhitespaceFacts.ALL_LIST.Contains(stringWalker.CurrentCharacter) ||
00260                 CssFacts.STYLE_BLOCK_START == stringWalker.CurrentCharacter)
00261             {
00262                 break;
00263             }
00264         }
00265 
00266         var identifierTextSpan = new TextEditorTextSpan(
00267             startingPositionIndex,
00268             stringWalker.PositionIndex,
00269             (byte)CssDecorationKind.Identifier,
00270             stringWalker.ResourceUri,
00271             stringWalker.SourceText);
00272 
00273         var identifierSyntax = new CssIdentifierSyntax(
00274             identifierTextSpan,
00275             ImmutableArray<ICssSyntax>.Empty);
00276 
00277         cssDocumentChildren.Add(identifierSyntax);
00278     }
