00066     public string GetCursorStyleCss()
00067     {
00068         var renderBatchLocal = _renderBatch;
00069         if (renderBatchLocal is null)
00070             return string.Empty;
00071         
00072         try
00073         {
00074             var measurements = renderBatchLocal.ViewModel.CharAndLineMeasurements;
00075 
00076             var leftInPixels = 0d;
00077 
00078             // Tab key column offset
00079             {
00080                 var tabsOnSameRowBeforeCursor = renderBatchLocal.Model.GetTabCountOnSameLineBeforeCursor(
00081                     renderBatchLocal.ViewModel.PrimaryCursor.LineIndex,
00082                     renderBatchLocal.ViewModel.PrimaryCursor.ColumnIndex);
00083 
00084                 // 1 of the character width is already accounted for
00085 
00086                 var extraWidthPerTabKey = TextEditorModel.TAB_WIDTH - 1;
00087 
00088                 leftInPixels += extraWidthPerTabKey *
00089                     tabsOnSameRowBeforeCursor *
00090                     measurements.CharacterWidth;
00091             }
00092 
00093             leftInPixels += measurements.CharacterWidth * renderBatchLocal.ViewModel.PrimaryCursor.ColumnIndex;
00094 
00095             var leftInPixelsInvariantCulture = leftInPixels.ToCssValue();
00096             var left = $"left: {leftInPixelsInvariantCulture}px;";
00097 
00098             var topInPixelsInvariantCulture = (measurements.LineHeight * renderBatchLocal.ViewModel.PrimaryCursor.LineIndex)
00099                 .ToCssValue();
00100 
00101             var top = $"top: {topInPixelsInvariantCulture}px;";
00102 
00103             var heightInPixelsInvariantCulture = measurements.LineHeight.ToCssValue();
00104             var height = $"height: {heightInPixelsInvariantCulture}px;";
00105 
00106             var widthInPixelsInvariantCulture = renderBatchLocal.Options.CursorWidthInPixels.ToCssValue();
00107             var width = $"width: {widthInPixelsInvariantCulture}px;";
00108 
00109             var keymapStyling = ((ITextEditorKeymap)renderBatchLocal.Options.Keymap).GetCursorCssStyleString(
00110                 renderBatchLocal.Model,
00111                 renderBatchLocal.ViewModel,
00112                 renderBatchLocal.Options);
00113             
00114             // This feels a bit hacky, exceptions are happening because the UI isn't accessing
00115             // the text editor in a thread safe way.
00116             //
00117             // When an exception does occur though, the cursor should receive a 'text editor changed'
00118             // event and re-render anyhow however.
00119             // 
00120             // So store the result of this method incase an exception occurs in future invocations,
00121             // to keep the cursor on screen while the state works itself out.
00122             return _previousGetCursorStyleCss = $"{left} {top} {height} {width} {keymapStyling}";
00123         }
00124         catch (LuthetusTextEditorException)
00125         {
00126             return _previousGetCursorStyleCss;
00127         }
00128     }
