00130     public string GetCaretRowStyleCss()
00131     {
00132         var renderBatchLocal = _renderBatch;
00133         if (renderBatchLocal is null)
00134             return string.Empty;
00135         
00136         try
00137         {
00138             var measurements = renderBatchLocal.ViewModel.CharAndLineMeasurements;
00139 
00140             var topInPixelsInvariantCulture = (measurements.LineHeight * renderBatchLocal.ViewModel.PrimaryCursor.LineIndex)
00141                 .ToCssValue();
00142 
00143             var top = $"top: {topInPixelsInvariantCulture}px;";
00144 
00145             var heightInPixelsInvariantCulture = measurements.LineHeight.ToCssValue();
00146             var height = $"height: {heightInPixelsInvariantCulture}px;";
00147 
00148             var widthOfBodyInPixelsInvariantCulture =
00149                 (renderBatchLocal.Model.MostCharactersOnASingleLineTuple.lineLength * measurements.CharacterWidth)
00150                 .ToCssValue();
00151 
00152             var width = $"width: {widthOfBodyInPixelsInvariantCulture}px;";
00153 
00154             // This feels a bit hacky, exceptions are happening because the UI isn't accessing
00155             // the text editor in a thread safe way.
00156             //
00157             // When an exception does occur though, the cursor should receive a 'text editor changed'
00158             // event and re-render anyhow however.
00159             // 
00160             // So store the result of this method incase an exception occurs in future invocations,
00161             // to keep the cursor on screen while the state works itself out.
00162             return _previousGetCaretRowStyleCss = $"{top} {width} {height}";
00163         }
00164         catch (LuthetusTextEditorException)
00165         {
00166             return _previousGetCaretRowStyleCss;
