00170     public string GetMenuStyleCss()
00171     {
00172         var renderBatchLocal = _renderBatch;
00173         if (renderBatchLocal is null)
00174             return string.Empty;
00175         
00176         try
00177         {
00178             var measurements = renderBatchLocal.ViewModel.CharAndLineMeasurements;
00179 
00180             var leftInPixels = 0d;
00181 
00182             // Tab key column offset
00183             {
00184                 var tabsOnSameRowBeforeCursor = renderBatchLocal.Model.GetTabCountOnSameLineBeforeCursor(
00185                     renderBatchLocal.ViewModel.PrimaryCursor.LineIndex,
00186                     renderBatchLocal.ViewModel.PrimaryCursor.ColumnIndex);
00187 
00188                 // 1 of the character width is already accounted for
00189                 var extraWidthPerTabKey = TextEditorModel.TAB_WIDTH - 1;
00190 
00191                 leftInPixels += extraWidthPerTabKey * tabsOnSameRowBeforeCursor *
00192                     measurements.CharacterWidth;
00193             }
00194 
00195             leftInPixels += measurements.CharacterWidth * renderBatchLocal.ViewModel.PrimaryCursor.ColumnIndex;
00196 
00197             var leftInPixelsInvariantCulture = leftInPixels.ToCssValue();
00198             var left = $"left: {leftInPixelsInvariantCulture}px;";
00199 
00200             var topInPixelsInvariantCulture = (measurements.LineHeight * (renderBatchLocal.ViewModel.PrimaryCursor.LineIndex + 1))
00201                 .ToCssValue();
00202 
00203             // Top is 1 row further than the cursor so it does not cover text at cursor position.
00204             var top = $"top: {topInPixelsInvariantCulture}px;";
00205 
00206             var minWidthInPixelsInvariantCulture = (measurements.CharacterWidth * 16).ToCssValue();
00207             var minWidth = $"min-Width: {minWidthInPixelsInvariantCulture}px;";
00208 
00209             var minHeightInPixelsInvariantCulture = (measurements.LineHeight * 4).ToCssValue();
00210             var minHeight = $"min-height: {minHeightInPixelsInvariantCulture}px;";
00211 
00212             // This feels a bit hacky, exceptions are happening because the UI isn't accessing
00213             // the text editor in a thread safe way.
00214             //
00215             // When an exception does occur though, the cursor should receive a 'text editor changed'
00216             // event and re-render anyhow however.
00217             // 
00218             // So store the result of this method incase an exception occurs in future invocations,
00219             // to keep the cursor on screen while the state works itself out.
00220             return _previousGetMenuStyleCss = $"{left} {top} {minWidth} {minHeight}";
00221         }
00222         catch (LuthetusTextEditorException)
00223         {
00224             return _previousGetMenuStyleCss;
00225         }
