00228     public async Task FocusAsync()
00229     {
00230         try
00231         {
00232             if (_cursorDisplayElementReference is not null)
00233             {
00234                 await _cursorDisplayElementReference.Value
00235                     .FocusAsync(preventScroll: true)
00236                     .ConfigureAwait(false);
00237             }
00238         }
00239         catch (Exception)
00240         {
00241             // 2023-04-18: The app has had a bug where it "freezes" and must be restarted.
00242             //             This bug is seemingly happening randomly. I have a suspicion
00243             //             that there are race-condition exceptions occurring with "FocusAsync"
00244             //             on an ElementReference.
00245         }
00246     }
