00278     private void Report(
00279         TextEditorDiagnosticLevel diagnosticLevel,
00280         string message,
00281         TextEditorTextSpan textSpan,
00282         Guid diagnosticId)
00283     {
00284         var compilerServiceDiagnosticDecorationKind = diagnosticLevel switch
00285         {
00286             TextEditorDiagnosticLevel.Hint => CompilerServiceDiagnosticDecorationKind.DiagnosticHint,
00287             TextEditorDiagnosticLevel.Suggestion => CompilerServiceDiagnosticDecorationKind.DiagnosticSuggestion,
00288             TextEditorDiagnosticLevel.Warning => CompilerServiceDiagnosticDecorationKind.DiagnosticWarning,
00289             TextEditorDiagnosticLevel.Error => CompilerServiceDiagnosticDecorationKind.DiagnosticError,
00290             TextEditorDiagnosticLevel.Other => CompilerServiceDiagnosticDecorationKind.DiagnosticOther,
00291             _ => CompilerServiceDiagnosticDecorationKind.DiagnosticOther,
00292         };
00293 
00294         textSpan = textSpan with
00295         {
00296             DecorationByte = (byte)compilerServiceDiagnosticDecorationKind
00297         };
00298 
00299         _diagnosticsBag.Add(new TextEditorDiagnostic(
00300             diagnosticLevel,
00301             message,
00302             textSpan,
00303             diagnosticId));
00304     }
00305 
00306     public void ClearByResourceUri(ResourceUri resourceUri)
00307     {
00308         var keep = _diagnosticsBag.Where(x => x.TextSpan.ResourceUri != resourceUri);
00309 
00310         _diagnosticsBag.Clear();
00311         _diagnosticsBag.AddRange(keep);
00312     }
