00170     private void PerformRemoveCSharpProjectReferenceFromSolution(
00171         TreeViewSolution treeViewSolution,
00172         TreeViewNamespacePath projectNode,
00173         ITerminal terminal,
00174         IDispatcher dispatcher,
00175         Func<Task> onAfterCompletion)
00176     {
00177         _backgroundTaskService.Enqueue(
00178             Key<IBackgroundTask>.NewKey(),
00179             ContinuousBackgroundTaskWorker.GetQueueKey(),
00180             "Remove C# Project Reference from Solution Action",
00181             () =>
00182             {
00183                 var workingDirectory = treeViewSolution.Item.NamespacePath.AbsolutePath.ParentDirectory!;
00184 
00185                 var formattedCommand = DotNetCliCommandFormatter.FormatRemoveCSharpProjectReferenceFromSolutionAction(
00186                     treeViewSolution.Item.NamespacePath.AbsolutePath.Value,
00187                     projectNode.Item.AbsolutePath.Value);
00188 
00189                 var terminalCommandRequest = new TerminalCommandRequest(
00190                     formattedCommand.Value,
00191                     workingDirectory.Value)
00192                 {
00193                     ContinueWithFunc = parsedCommand => onAfterCompletion.Invoke()
00194                 };
00195 
00196                 terminal.EnqueueCommand(terminalCommandRequest);
00197                 return Task.CompletedTask;
00198             });
00199     }
