00201     public void PerformAddProjectToProjectReference(
00202         TreeViewNamespacePath projectReceivingReference,
00203         ITerminal terminal,
00204         IDispatcher dispatcher,
00205         IdeBackgroundTaskApi ideBackgroundTaskApi,
00206         Func<Task> onAfterCompletion)
00207     {
00208         ideBackgroundTaskApi.InputFile.RequestInputFileStateForm(
00209             $"Add Project reference to {projectReceivingReference.Item.AbsolutePath.NameWithExtension}",
00210             referencedProject =>
00211             {
00212                 if (referencedProject is null)
00213                     return Task.CompletedTask;
00214 
00215                 var formattedCommand = DotNetCliCommandFormatter.FormatAddProjectToProjectReference(
00216                     projectReceivingReference.Item.AbsolutePath.Value,
00217                     referencedProject.Value);
00218 
00219                 var terminalCommandRequest = new TerminalCommandRequest(
00220                     formattedCommand.Value,
00221                     null)
00222                 {
00223                     ContinueWithFunc = parsedCommand =>
00224                     {
00225                         NotificationHelper.DispatchInformative("Add Project Reference", $"Modified {projectReceivingReference.Item.AbsolutePath.NameWithExtension} to have a reference to {referencedProject.NameWithExtension}", _commonComponentRenderers, dispatcher, TimeSpan.FromSeconds(7));
00226                         return onAfterCompletion.Invoke();
00227                     }
00228                 };
00229 
00230                 terminal.EnqueueCommand(terminalCommandRequest);
00231                 return Task.CompletedTask;
00232             },
00233             absolutePath =>
00234             {
00235                 if (absolutePath is null || absolutePath.IsDirectory)
00236                     return Task.FromResult(false);
00237 
00238                 return Task.FromResult(
00239                     absolutePath.ExtensionNoPeriod.EndsWith(ExtensionNoPeriodFacts.C_SHARP_PROJECT));
00240             },
00241             (new[]
00242             {
00243                 new InputFilePattern(
00244                     "C# Project",
00245                     absolutePath => absolutePath.ExtensionNoPeriod.EndsWith(ExtensionNoPeriodFacts.C_SHARP_PROJECT))
00246             }).ToImmutableArray());
00247     }
