00314     private Task ParseSolution(Key<DotNetSolutionModel> dotNetSolutionModelKey)
00315     {
00316         var dotNetSolutionState = _dotNetSolutionStateWrap.Value;
00317 
00318         var dotNetSolutionModel = dotNetSolutionState.DotNetSolutionsList.FirstOrDefault(
00319             x => x.Key == dotNetSolutionModelKey);
00320 
00321         if (dotNetSolutionModel is null)
00322             return Task.CompletedTask;
00323             
00324         _ = Task.Run(async () =>
00325         {
00326             var cancellationTokenSource = new CancellationTokenSource();
00327             var cancellationToken = cancellationTokenSource.Token;
00328             
00329             var progressBarModel = new ProgressBarModel(0, "parsing...")
00330             {
00331                 OnCancelFunc = () =>
00332                 {
00333                     cancellationTokenSource.Cancel();
00334                     cancellationTokenSource.Dispose();
00335                     return Task.CompletedTask;
00336                 }
00337             };
00338 
00339             NotificationHelper.DispatchProgress(
00340                 $"Parse: {dotNetSolutionModel.AbsolutePath.NameWithExtension}",
00341                 progressBarModel,
00342                 _commonComponentRenderers,
00343                 _dispatcher,
00344                 TimeSpan.FromMilliseconds(-1));
00345                 
00346             // var progressThrottle = new Throttle(TimeSpan.FromMilliseconds(100));
00347             var progressThrottle = new ThrottleOptimized<(ParseSolutionStageKind StageKind, double? Progress, string? MessageOne, string? MessageTwo)>(TimeSpan.FromMilliseconds(200), (tuple, _) =>
00348             {
00349                 switch (tuple.StageKind)
00350                 {
00351                     case ParseSolutionStageKind.A:
00352                         progressBarModel.SetProgress(tuple.Progress, tuple.MessageOne);
00353                         return Task.CompletedTask;
00354                     case ParseSolutionStageKind.B:
00355                         progressBarModel.SetProgress(tuple.Progress, tuple.MessageOne, tuple.MessageTwo);
00356                         progressBarModel.Dispose();
00357                         return Task.CompletedTask;
00358                     case ParseSolutionStageKind.C:
00359                         progressBarModel.SetProgress(tuple.Progress, tuple.MessageOne);
00360                         progressBarModel.Dispose();
00361                         return Task.CompletedTask;
00362                     case ParseSolutionStageKind.D:
00363                         progressBarModel.SetProgress(tuple.Progress, tuple.MessageOne, tuple.MessageTwo);
00364                         return Task.CompletedTask;
00365                     case ParseSolutionStageKind.E:
00366                         progressBarModel.SetProgress(tuple.Progress, tuple.MessageOne, tuple.MessageTwo);
00367                         return Task.CompletedTask;
00368                     default:
00369                         return Task.CompletedTask;
00370                 }
00371             });
00372         
00373             try
00374             {
00375                 if (_textEditorService.TextEditorConfig.RegisterModelFunc is null)
00376                     return;
00377                 
00378                 progressThrottle.Run((ParseSolutionStageKind.A, 0.05, "Discovering projects...", null));
00379                 
00380                 foreach (var project in dotNetSolutionModel.DotNetProjectList)
00381                 {
00382                     RegisterStartupControl(project);
00383                 
00384                     var resourceUri = new ResourceUri(project.AbsolutePath.Value);
00385 
00386                     if (!await _fileSystemProvider.File.ExistsAsync(resourceUri.Value))
00387                         continue; // TODO: This can still cause a race condition exception if the file is removed before the next line runs.
00388 
00389                     var registerModelArgs = new RegisterModelArgs(resourceUri, _serviceProvider)
00390                     {
00391                         ShouldBlockUntilBackgroundTaskIsCompleted = true,
00392                     };
00393 
00394                     await _textEditorService.TextEditorConfig.RegisterModelFunc.Invoke(registerModelArgs)
00395                         .ConfigureAwait(false);
00396                 }
00397 
00398                 var previousStageProgress = 0.05;
00399                 var dotNetProjectListLength = dotNetSolutionModel.DotNetProjectList.Length;
00400                 var projectsParsedCount = 0;
00401                 foreach (var project in dotNetSolutionModel.DotNetProjectList)
00402                 {
00403                     // foreach project in solution
00404                     //  foreach C# file in project
00405                     //      EnqueueBackgroundTask(async () =>
00406                     //      {
00407                     //          ParseCSharpFile();
00408                     //          UpdateProgressBar();
00409                     //      });
00410                     //
00411                     // Treat every project as an equal weighting with relation to remaining percent to complete
00412                     // on the progress bar.
00413                     //
00414                     // If the project were to be parsed, how much would it move the percent progress completed by?
00415                     //
00416                     // Then, in order to see progress while each C# file in the project gets parsed,
00417                     // multiply the percent progress this project can provide by the proportion
00418                     // of the project's C# files which have been parsed.
00419                     var maximumProgressAvailableToProject = (1 - previousStageProgress) * ((double)1.0 / dotNetProjectListLength);
00420                     var currentProgress = Math.Min(1.0, previousStageProgress + maximumProgressAvailableToProject * projectsParsedCount);
00421 
00422                     // This 'SetProgress' is being kept out the throttle, since it sets message 1
00423                     // whereas the per class progress updates set message 2.
00424                     //
00425                     // Otherwise an update to message 2 could result in this message 1 update never being written.
00426                     progressBarModel.SetProgress(
00427                         currentProgress,
00428                         $"{projectsParsedCount + 1}/{dotNetProjectListLength}: {project.AbsolutePath.NameWithExtension}");
00429                     
00430                     cancellationToken.ThrowIfCancellationRequested();
00431 
00432                     await DiscoverClassesInProject(project, progressBarModel, progressThrottle, currentProgress, maximumProgressAvailableToProject);
00433                     projectsParsedCount++;
00434                 }
00435 
00436                 progressThrottle.Run((ParseSolutionStageKind.B, 1, $"Finished parsing: {dotNetSolutionModel.AbsolutePath.NameWithExtension}", string.Empty));
00437             }
00438             catch (Exception e)
00439             {
00440                 if (e is OperationCanceledException)
00441                     progressBarModel.IsCancelled = true;
00442                     
00443                 var currentProgress = progressBarModel.GetProgress();
00444                 
00445                 progressThrottle.Run((ParseSolutionStageKind.C, currentProgress, e.ToString(), null));
00446             }
00447         });
00448 
00449         return Task.CompletedTask;
00450     }
