00452     private async Task DiscoverClassesInProject(
00453         IDotNetProject dotNetProject,
00454         ProgressBarModel progressBarModel,
00455         ThrottleOptimized<(ParseSolutionStageKind StageKind, double? Progress, string? MessageOne, string? MessageTwo)> progressThrottle,
00456         double currentProgress,
00457         double maximumProgressAvailableToProject)
00458     {
00459         if (!await _fileSystemProvider.File.ExistsAsync(dotNetProject.AbsolutePath.Value))
00460             return; // TODO: This can still cause a race condition exception if the file is removed before the next line runs.
00461 
00462         var parentDirectory = dotNetProject.AbsolutePath.ParentDirectory;
00463         if (parentDirectory is null)
00464             return;
00465 
00466         var startingAbsolutePathForSearch = parentDirectory.Value;
00467         var discoveredFileList = new List<string>();
00468 
00469         progressThrottle.Run((ParseSolutionStageKind.D, null, null, "discovering files"));
00470         
00471         await DiscoverFilesRecursively(startingAbsolutePathForSearch, discoveredFileList, true).ConfigureAwait(false);
00472 
00473         await ParseClassesInProject(
00474             dotNetProject,
00475             progressBarModel,
00476             progressThrottle,
00477             currentProgress,
00478             maximumProgressAvailableToProject,
00479             discoveredFileList);
00480 
00481         async Task DiscoverFilesRecursively(string directoryPathParent, List<string> discoveredFileList, bool isFirstInvocation)
00482         {
00483             var directoryPathChildList = await _fileSystemProvider.Directory.GetDirectoriesAsync(
00484                     directoryPathParent,
00485                     CancellationToken.None)
00486                 .ConfigureAwait(false);
00487 
00488             var filePathChildList = await _fileSystemProvider.Directory.GetFilesAsync(
00489                     directoryPathParent,
00490                     CancellationToken.None)
00491                 .ConfigureAwait(false);
00492 
00493             foreach (var filePathChild in filePathChildList)
00494             {
00495                 if (filePathChild.EndsWith(".cs"))
00496                     discoveredFileList.Add(filePathChild);
00497             }
00498 
00499             //var progressMessage = progressBarModel.Message ?? string.Empty;
00500 
00501             foreach (var directoryPathChild in directoryPathChildList)
00502             {
00503                 if (directoryPathChild.Contains(".git") ||
00504                     directoryPathChild.Contains(".vs") ||
00505                     directoryPathChild.Contains(".vscode") ||
00506                     directoryPathChild.Contains(".idea") ||
00507                     directoryPathChild.Contains("bin") ||
00508                     directoryPathChild.Contains("obj"))
00509                 {
00510                     continue;
00511                 }
00512 
00513                 //if (isFirstInvocation)
00514                 //{
00515                 //  var currentProgress = progressBarModel.GetProgress();
00516                 // progressThrottle.Run(_ => 
00517                 // {
00518                 //  progressBarModel.SetProgress(currentProgress, $"{directoryPathChild} " + progressMessage);
00519                 //  return Task.CompletedTask;
00520                 // });
00521                 //  
00522                 //}
00523 
00524                 await DiscoverFilesRecursively(directoryPathChild, discoveredFileList, isFirstInvocation: false).ConfigureAwait(false);
00525             }
00526         }
00527     }
