00529     private async Task ParseClassesInProject(
00530         IDotNetProject dotNetProject,
00531         ProgressBarModel progressBarModel,
00532         ThrottleOptimized<(ParseSolutionStageKind StageKind, double? Progress, string? MessageOne, string? MessageTwo)> progressThrottle,
00533         double currentProgress,
00534         double maximumProgressAvailableToProject,
00535         List<string> discoveredFileList)
00536     {
00537         var fileParsedCount = 0;
00538         
00539         foreach (var file in discoveredFileList)
00540         {
00541             var fileAbsolutePath = _environmentProvider.AbsolutePathFactory(file, false);
00542 
00543             var progress = currentProgress + maximumProgressAvailableToProject * (fileParsedCount / (double)discoveredFileList.Count);
00544 
00545             progressThrottle.Run((ParseSolutionStageKind.E, progress, null, $"{fileParsedCount + 1}/{discoveredFileList.Count}: {fileAbsolutePath.NameWithExtension}"));
00546 
00547             var resourceUri = new ResourceUri(file);
00548             
00549             var registerModelArgs = new RegisterModelArgs(resourceUri, _serviceProvider)
00550             {
00551                 ShouldBlockUntilBackgroundTaskIsCompleted = true,
00552             };
00553 
00554             await _textEditorService.TextEditorConfig.RegisterModelFunc.Invoke(registerModelArgs)
00555                 .ConfigureAwait(false);
00556                 
00557             fileParsedCount++;
00558         }
00559     }
