00585             _fileSystemProvider,
00586             _environmentProvider,
00587             true,
00588             true);
00589 
00590         await rootNode.LoadChildListAsync().ConfigureAwait(false);
00591 
00592         if (!_treeViewService.TryGetTreeViewContainer(DotNetSolutionState.TreeViewSolutionExplorerStateKey, out _))
00593         {
00594             _treeViewService.RegisterTreeViewContainer(new TreeViewContainer(
00595                 DotNetSolutionState.TreeViewSolutionExplorerStateKey,
00596                 rootNode,
00597                 new TreeViewNoType[] { rootNode }.ToImmutableList()));
00598         }
00599         else
00600         {
00601             _treeViewService.SetRoot(DotNetSolutionState.TreeViewSolutionExplorerStateKey, rootNode);
00602 
00603             _treeViewService.SetActiveNode(
00604                 DotNetSolutionState.TreeViewSolutionExplorerStateKey,
00605                 rootNode,
00606                 true,
00607                 false);
00608         }
00609 
00610         if (dotNetSolutionModel is null)
00611             return;
00612 
00613         _dispatcher.Dispatch(ConstructModelReplacement(
00614             dotNetSolutionModel.Key,
00615             dotNetSolutionModel));
00616     }
