00618     private void RegisterStartupControl(IDotNetProject project)
00619     {
00620         _dispatcher.Dispatch(new StartupControlState.RegisterStartupControlAction(
00621             new StartupControlModel(
00622                 Key<IStartupControlModel>.NewKey(),
00623                 project.DisplayName,
00624                 project.AbsolutePath.Value,
00625                 project.AbsolutePath,
00626                 null,
00627                 null,
00628                 startupControlModel => StartButtonOnClick(startupControlModel, project),
00629                 StopButtonOnClick)));
00630     }
00631     
00632     private Task StartButtonOnClick(IStartupControlModel interfaceStartupControlModel, IDotNetProject project)
00633     {
00634         var startupControlModel = (StartupControlModel)interfaceStartupControlModel;
00635         
00636         var ancestorDirectory = project.AbsolutePath.ParentDirectory;
00637 
00638         if (ancestorDirectory is null)
00639             return Task.CompletedTask;
00640 
00641         var formattedCommand = DotNetCliCommandFormatter.FormatStartProjectWithoutDebugging(
00642             project.AbsolutePath);
00643             
00644         var terminalCommandRequest = new TerminalCommandRequest(
00645             formattedCommand.Value,
00646             ancestorDirectory.Value,
00647             _newDotNetSolutionTerminalCommandRequestKey)
00648         {
00649             BeginWithFunc = parsedCommand =>
00650             {
00651                 _dotNetCliOutputParser.ParseOutputEntireDotNetRun(
00652                     string.Empty,
00653                     "Run-Project_started");
00654                     
00655                 return Task.CompletedTask;
00656             },
00657             ContinueWithFunc = parsedCommand =>
00658             {
00659                 startupControlModel.ExecutingTerminalCommandRequest = null;
00660                 _dispatcher.Dispatch(new StartupControlState.StateChangedAction());
00661             
00662                 _dotNetCliOutputParser.ParseOutputEntireDotNetRun(
00663                     parsedCommand.OutputCache.ToString(),
00664                     "Run-Project_completed");
00665                     
00666                 return Task.CompletedTask;
00667             }
00668         };
00669         
00670         startupControlModel.ExecutingTerminalCommandRequest = terminalCommandRequest;
00671         
00672         _terminalStateWrap.Value.TerminalMap[TerminalFacts.EXECUTION_KEY].EnqueueCommand(terminalCommandRequest);
00673         return Task.CompletedTask;
00674     }
00675     
00676     private Task StopButtonOnClick(IStartupControlModel interfaceStartupControlModel)
00677     {
00678         var startupControlModel = (StartupControlModel)interfaceStartupControlModel;
00679         
00680         _terminalStateWrap.Value.TerminalMap[TerminalFacts.EXECUTION_KEY].KillProcess();
00681         startupControlModel.ExecutingTerminalCommandRequest = null;
00682         
00683         _dispatcher.Dispatch(new StartupControlState.StateChangedAction());
00684         return Task.CompletedTask;
00685     }
