00687     public void Website_AddExistingProjectToSolution(
00688         Key<DotNetSolutionModel> dotNetSolutionModelKey,
00689         string projectTemplateShortName,
00690         string cSharpProjectName,
00691         IAbsolutePath cSharpProjectAbsolutePath)
00692     {
00693         _backgroundTaskService.Enqueue(
00694             Key<IBackgroundTask>.NewKey(),
00695             ContinuousBackgroundTaskWorker.GetQueueKey(),
00696             "Add Existing-Project To Solution",
00697             async () => await Website_AddExistingProjectToSolutionAsync(
00698                 dotNetSolutionModelKey,
00699                 projectTemplateShortName,
00700                 cSharpProjectName,
00701                 cSharpProjectAbsolutePath));
00702     }
00703 
00704     private async Task Website_AddExistingProjectToSolutionAsync(
00705         Key<DotNetSolutionModel> dotNetSolutionModelKey,
00706         string projectTemplateShortName,
00707         string cSharpProjectName,
00708         IAbsolutePath cSharpProjectAbsolutePath)
00709     {
00710         var inDotNetSolutionModel = _dotNetSolutionStateWrap.Value.DotNetSolutionsList.FirstOrDefault(
00711             x => x.Key == dotNetSolutionModelKey);
00712 
00713         if (inDotNetSolutionModel is null)
00714             return;
00715 
00716         var projectTypeGuid = WebsiteProjectTemplateFacts.GetProjectTypeGuid(
00717             projectTemplateShortName);
00718 
00719         var relativePathFromSlnToProject = PathHelper.GetRelativeFromTwoAbsolutes(
00720             inDotNetSolutionModel.NamespacePath.AbsolutePath,
00721             cSharpProjectAbsolutePath,
00722             _environmentProvider);
00723 
00724         var projectIdGuid = Guid.NewGuid();
00725 
00726         var dotNetSolutionModelBuilder = new DotNetSolutionModelBuilder(inDotNetSolutionModel);
00727 
00728         var cSharpProject = new CSharpProjectModel(
00729             cSharpProjectName,
00730             projectTypeGuid,
00731             relativePathFromSlnToProject,
00732             projectIdGuid,
00733             // TODO: 'openAssociatedGroupToken' gets set when 'AddDotNetProject(...)' is ran, which is hacky and should be changed. Until then passing in 'null!'
00734             default,
00735             null,
00736             cSharpProjectAbsolutePath);
00737 
00738         dotNetSolutionModelBuilder.AddDotNetProject(cSharpProject, _environmentProvider);
00739 
00740         var outDotNetSolutionModel = dotNetSolutionModelBuilder.Build();
00741 
00742         await _fileSystemProvider.File.WriteAllTextAsync(
00743                 outDotNetSolutionModel.NamespacePath.AbsolutePath.Value,
00744                 outDotNetSolutionModel.SolutionFileContents)
00745             .ConfigureAwait(false);
00746 
00747         var solutionTextEditorModel = _textEditorService.ModelApi.GetOrDefault(
00748             new ResourceUri(inDotNetSolutionModel.NamespacePath.AbsolutePath.Value));
00749 
00750         if (solutionTextEditorModel is not null)
00751         {
00752             _textEditorService.PostUnique(
00753                 nameof(Website_AddExistingProjectToSolutionAsync),
00754                 editContext =>
00755                 {
00756                     var modelModifier = editContext.GetModelModifier(solutionTextEditorModel.ResourceUri);
00757                     if (modelModifier is null)
00758                         return Task.CompletedTask;
00759                 
00760                     _textEditorService.ModelApi.Reload(
00761                         editContext,
00762                         modelModifier,
00763                         outDotNetSolutionModel.SolutionFileContents,
00764                         DateTime.UtcNow);
00765                     return Task.CompletedTask;
00766                 });
00767         }
00768 
00769         // TODO: Putting a hack for now to overwrite if somehow model was registered already
00770         _dispatcher.Dispatch(ConstructModelReplacement(
00771             outDotNetSolutionModel.Key,
00772             outDotNetSolutionModel));
00773 
00774         await SetDotNetSolutionTreeViewAsync(outDotNetSolutionModel.Key).ConfigureAwait(false);
00775     }
00776 
00778     public interface IWithAction
00779     {
00780     }
00781 
00783     public record WithAction(Func<DotNetSolutionState, DotNetSolutionState> WithFunc)
00784         : IWithAction;
00785 
00786     public static IWithAction ConstructModelReplacement(
00787             Key<DotNetSolutionModel> dotNetSolutionModelKey,
00788             DotNetSolutionModel outDotNetSolutionModel)
00789     {
00790         return new WithAction(dotNetSolutionState =>
00791         {
00792             var indexOfSln = dotNetSolutionState.DotNetSolutionsList.FindIndex(
00793                 sln => sln.Key == dotNetSolutionModelKey);
00794 
00795             if (indexOfSln == -1)
00796                 return dotNetSolutionState;
00797 
00798             var outDotNetSolutions = dotNetSolutionState.DotNetSolutionsList.SetItem(
00799                 indexOfSln,
00800                 outDotNetSolutionModel);
00801 
00802             return dotNetSolutionState with
00803             {
00804                 DotNetSolutionsList = outDotNetSolutions
00805             };
00806         });
00807     }
