00112     private string GetTitle()
00113     {
00114         if (TextEditorService is null)
00115             return "TextEditorService was null";
00116 
00117         var model = TextEditorService.ViewModelApi.GetModelOrDefault(ViewModelKey);
00118         var viewModel = TextEditorService.ViewModelApi.GetOrDefault(ViewModelKey);
00119 
00120         if (viewModel is null)
00121         {
00122             return "ViewModel not found";
00123         }
00124         else if (model is null)
00125         {
00126             return "Model not found";
00127         }
00128         else
00129         {
00130             var displayName = viewModel.GetTabDisplayNameFunc?.Invoke(model)
00131                 ?? model.ResourceUri.Value;
00132 
00133             if (model.IsDirty)
00134                 displayName += '*';
00135 
00136             return displayName;
00137         }
00138     }
