00262                         // Already a dialog, so nothing needs to be done here.
00263                         return Task.CompletedTask;
00264                     }
00265 
00266                     TabGroup = null;
00267                 }
00268 
00269                 DialogService.RegisterDialogRecord(this);
00270             }
00271 
