00273             {
00274                 // Check if to-be group is the same as current group.
00275                 {
00276                     if (localTextEditorGroup is not null)
00277                     {
00278                         if (localTextEditorGroup.GroupKey == textEditorGroupDropzone.TextEditorGroupKey)
00279                             return Task.CompletedTask;
00280                     }
00281                 }
00282 
00283                 // Delete the current UI
00284                 {
00285                     if (localTextEditorGroup is not null)
00286                     {
00287                         TextEditorService.GroupApi.RemoveViewModel(
00288                             localTextEditorGroup.GroupKey,
00289                             ViewModelKey);
00290                     }
