00079     public void ShowInputFile()
00080     {
00081         _ideBackgroundTaskApi.InputFile.RequestInputFileStateForm(
00082             "TextEditor",
00083             absolutePath =>
00084             {
00085                 return _textEditorService.OpenInEditorAsync(
00086                     absolutePath.Value,
00087                     true,
00088                     null,
00089                     new Category("main"),
00090                     Key<TextEditorViewModel>.NewKey());
00091             },
00092             absolutePath =>
00093             {
00094                 if (absolutePath is null || absolutePath.IsDirectory)
00095                     return Task.FromResult(false);
00096 
00097                 return Task.FromResult(true);
00098             },
00099             new[]
00100             {
00101                     new InputFilePattern("File", absolutePath => !absolutePath.IsDirectory)
00102             }.ToImmutableArray());
00103     }
