00105     public async Task RegisterModelFunc(RegisterModelArgs registerModelArgs)
00106     {
00107         var model = _textEditorService.ModelApi.GetOrDefault(registerModelArgs.ResourceUri);
00108 
00109         if (model is null)
00110         {
00111             var resourceUri = registerModelArgs.ResourceUri;
00112 
00113             var fileLastWriteTime = await _fileSystemProvider.File
00114                 .GetLastWriteTimeAsync(resourceUri.Value)
00115                 .ConfigureAwait(false);
00116 
00117             var content = await _fileSystemProvider.File
00118                 .ReadAllTextAsync(resourceUri.Value)
00119                 .ConfigureAwait(false);
00120 
00121             var absolutePath = _environmentProvider.AbsolutePathFactory(resourceUri.Value, false);
00122             var decorationMapper = _decorationMapperRegistry.GetDecorationMapper(absolutePath.ExtensionNoPeriod);
00123             var compilerService = _compilerServiceRegistry.GetCompilerService(absolutePath.ExtensionNoPeriod);
00124 
00125             model = new TextEditorModel(
00126                 resourceUri,
00127                 fileLastWriteTime,
00128                 absolutePath.ExtensionNoPeriod,
00129                 content,
00130                 decorationMapper,
00131                 compilerService);
00132                 
00133             var modelModifier = new TextEditorModelModifier(model);
00134             modelModifier.PerformRegisterPresentationModelAction(CompilerServiceDiagnosticPresentationFacts.EmptyPresentationModel);
00135             modelModifier.PerformRegisterPresentationModelAction(FindOverlayPresentationFacts.EmptyPresentationModel);
00136             modelModifier.PerformRegisterPresentationModelAction(DiffPresentationFacts.EmptyInPresentationModel);
00137             modelModifier.PerformRegisterPresentationModelAction(DiffPresentationFacts.EmptyOutPresentationModel);
00138             
00139             model = modelModifier.ToModel();
00140 
00141             _textEditorService.ModelApi.RegisterCustom(model);
00142             
00143             model.CompilerService.RegisterResource(
00144                 model.ResourceUri,
00145                 shouldTriggerResourceWasModified: false);
00146                 
00147             var uniqueTextEditorWork = new UniqueTextEditorWork(
00148                 nameof(compilerService.ParseAsync),
00149                 editContext =>
00150                 {
00151                     var modelModifier = editContext.GetModelModifier(resourceUri);
00152     
00153                     if (modelModifier is null)
00154                         return Task.CompletedTask;
00155     
00156                     return compilerService.ParseAsync(editContext, modelModifier);
00157                 });
00158             
00159             if (registerModelArgs.ShouldBlockUntilBackgroundTaskIsCompleted)
00160                 await _textEditorService.PostAsync(uniqueTextEditorWork).ConfigureAwait(false);
00161             else
00162                 _textEditorService.Post(uniqueTextEditorWork);
00163         }
00164 
00165         await CheckIfContentsWereModifiedAsync(
00166                 _dispatcher,
00167                 registerModelArgs.ResourceUri.Value,
00168                 model)
00169             .ConfigureAwait(false);
00170     }
