00172     public Task<Key<TextEditorViewModel>> TryRegisterViewModelFunc(TryRegisterViewModelArgs registerViewModelArgs)
00173     {
00174         try
00175         {
00176             var model = _textEditorService.ModelApi.GetOrDefault(registerViewModelArgs.ResourceUri);
00177     
00178             if (model is null)
00179             {
00180                 NotificationHelper.DispatchDebugMessage(nameof(TryRegisterViewModelFunc), () => "model is null: " + registerViewModelArgs.ResourceUri.Value, _commonComponentRenderers, _dispatcher, TimeSpan.FromSeconds(4));
00181                 return Task.FromResult(Key<TextEditorViewModel>.Empty);
00182             }
00183     
00184             var viewModel = _textEditorService.ModelApi
00185                 .GetViewModelsOrEmpty(registerViewModelArgs.ResourceUri)
00186                 .FirstOrDefault(x => x.Category == registerViewModelArgs.Category);
00187     
00188             if (viewModel is not null)
00189                 return Task.FromResult(viewModel.ViewModelKey);
00190     
00191             var viewModelKey = Key<TextEditorViewModel>.NewKey();
00192     
00193             viewModel = new TextEditorViewModel(
00194                 viewModelKey,
00195                 registerViewModelArgs.ResourceUri,
00196                 _textEditorService,
00197                 _dispatcher,
00198                 _dialogService,
00199                 _jsRuntime,
00200                 VirtualizationResult<List<RichCharacter>>.GetEmptyRichCharacters(),
00201                 new TextEditorDimensions(0, 0, 0, 0),
00202                 new ScrollbarDimensions(0, 0, 0, 0, 0),
00203                 new CharAndLineMeasurements(0, 0),
00204                 false,
00205                 registerViewModelArgs.Category);
00206     
00207             var firstPresentationLayerKeys = new[]
00208             {
00209                 CompilerServiceDiagnosticPresentationFacts.PresentationKey,
00210                 FindOverlayPresentationFacts.PresentationKey,
00211             }.ToImmutableArray();
00212     
00213             var absolutePath = _environmentProvider.AbsolutePathFactory(
00214                 registerViewModelArgs.ResourceUri.Value,
00215                 false);
00216                 
00217             viewModel.UnsafeState.ShouldSetFocusAfterNextRender = registerViewModelArgs.ShouldSetFocusToEditor;
00218         
00219             viewModel = viewModel with
00220             {
00221                 OnSaveRequested = HandleOnSaveRequested,
00222                 GetTabDisplayNameFunc = _ => absolutePath.NameWithExtension,
00223                 FirstPresentationLayerKeysList = firstPresentationLayerKeys.ToImmutableList()
00224             };
00225             
00226             _textEditorService.ViewModelApi.Register(viewModel);
00227     
00228             return Task.FromResult(viewModelKey);
