00230             void HandleOnSaveRequested(ITextEditorModel innerTextEditor)
00231             {
00232                 var innerContent = innerTextEditor.GetAllText();
00233     
00234                 var cancellationToken = model.TextEditorSaveFileHelper.GetCancellationToken();
00235     
00236                 _ideBackgroundTaskApi.FileSystem.SaveFile(
00237                     absolutePath,
00238                     innerContent,
00239                     writtenDateTime =>
00240                     {
00241                         if (writtenDateTime is not null)
00242                         {
00243                             _textEditorService.PostUnique(
00244                                 nameof(HandleOnSaveRequested),
00245                                 editContext =>
00246                                 {
00247                                     var modelModifier = editContext.GetModelModifier(innerTextEditor.ResourceUri);
00248                                     if (modelModifier is null)
00249                                         return Task.CompletedTask;
00250                                 
00251                                     _textEditorService.ModelApi.SetResourceData(
00252                                         editContext,
00253                                         modelModifier,
00254                                         writtenDateTime.Value);
00255                                     return Task.CompletedTask;
00256                                 });
00257                         }
00258     
00259                         return Task.CompletedTask;
00260                     },
00261                     cancellationToken);
00262             }
00263         }
00264         catch (Exception e)
00265         {
00266             NotificationHelper.DispatchError(
00267                 nameof(TryRegisterViewModelFunc),
00268                 e.ToString(),
00269                 _commonComponentRenderers,
00270                 _dispatcher,
00271                 TimeSpan.FromSeconds(6));
00272             return Task.FromResult(Key<TextEditorViewModel>.Empty);
00273         }
00274     }
