00276     public async Task<bool> TryShowViewModelFunc(TryShowViewModelArgs showViewModelArgs)
00277     {
00278         _textEditorService.GroupApi.Register(EditorTextEditorGroupKey);
00279 
00280         var viewModel = _textEditorService.ViewModelApi.GetOrDefault(showViewModelArgs.ViewModelKey);
00281 
00282         if (viewModel is null)
00283             return false;
00284 
00285         if (viewModel.Category == new Category("main") &&
00286             showViewModelArgs.GroupKey == Key<TextEditorGroup>.Empty)
00287         {
00288             showViewModelArgs = new TryShowViewModelArgs(
00289                 showViewModelArgs.ViewModelKey,
00290                 EditorTextEditorGroupKey,
00291                 showViewModelArgs.ShouldSetFocusToEditor,
00292                 showViewModelArgs.ServiceProvider);
00293         }
00294 
00295         if (showViewModelArgs.ViewModelKey == Key<TextEditorViewModel>.Empty ||
00296             showViewModelArgs.GroupKey == Key<TextEditorGroup>.Empty)
00297         {
00298             return false;
00299         }
00300 
00301         _textEditorService.GroupApi.AddViewModel(
00302             showViewModelArgs.GroupKey,
00303             showViewModelArgs.ViewModelKey);
00304 
00305         _textEditorService.GroupApi.SetActiveViewModel(
00306             showViewModelArgs.GroupKey,
00307             showViewModelArgs.ViewModelKey);
00308             
00309         if (showViewModelArgs.ShouldSetFocusToEditor)
00310         {
00311             _textEditorService.PostUnique(nameof(TryShowViewModelFunc), editContext =>
00312             {
00313                 var viewModelModifier = editContext.GetViewModelModifier(showViewModelArgs.ViewModelKey);
00314                 
00315                 viewModelModifier.ViewModel.UnsafeState.ShouldSetFocusAfterNextRender =
00316                     showViewModelArgs.ShouldSetFocusToEditor;
00317                     
00318                 return viewModel.FocusAsync();
00319             });
00320         }
00321 
00322         return true;
00323     }
