00325     private async Task CheckIfContentsWereModifiedAsync(
00326         IDispatcher dispatcher,
00327         string inputFileAbsolutePathString,
00328         TextEditorModel textEditorModel)
00329     {
00330         var fileLastWriteTime = await _fileSystemProvider.File
00331             .GetLastWriteTimeAsync(inputFileAbsolutePathString)
00332             .ConfigureAwait(false);
00333 
00334         if (fileLastWriteTime > textEditorModel.ResourceLastWriteTime &&
00335             _ideComponentRenderers.BooleanPromptOrCancelRendererType is not null)
00336         {
00337             var notificationInformativeKey = Key<IDynamicViewModel>.NewKey();
00338 
00339             var notificationInformative = new NotificationViewModel(
00340                 notificationInformativeKey,
00341                 "File contents were modified on disk",
00342                 _ideComponentRenderers.BooleanPromptOrCancelRendererType,
00343                 new Dictionary<string, object?>
00344                 {
00345                         {
00346                             nameof(IBooleanPromptOrCancelRendererType.Message),
00347                             "File contents were modified on disk"
00348                         },
00349                         {
00350                             nameof(IBooleanPromptOrCancelRendererType.AcceptOptionTextOverride),
00351                             "Reload"
00352                         },
00353                         {
00354                             nameof(IBooleanPromptOrCancelRendererType.OnAfterAcceptFunc),
00355                             new Func<Task>(() =>
00356                             {
00357                                 _backgroundTaskService.Enqueue(
00358                                         Key<IBackgroundTask>.NewKey(),
00359                                         ContinuousBackgroundTaskWorker.GetQueueKey(),
00360                                         "Check If Contexts Were Modified",
00361                                         async () =>
00362                                         {
00363                                             dispatcher.Dispatch(new NotificationState.DisposeAction(
00364                                                 notificationInformativeKey));
00365 
00366                                             var content = await _fileSystemProvider.File
00367                                                 .ReadAllTextAsync(inputFileAbsolutePathString)
00368                                                 .ConfigureAwait(false);
00369 
00370                                             _textEditorService.PostUnique(
00371                                                 nameof(CheckIfContentsWereModifiedAsync),
00372                                                 editContext =>
00373                                                 {
00374                                                     var modelModifier = editContext.GetModelModifier(textEditorModel.ResourceUri);
00375                                                     if (modelModifier is null)
00376                                                         return Task.CompletedTask;
00377                                                 
00378                                                     _textEditorService.ModelApi.Reload(
00379                                                         editContext,
00380                                                         modelModifier,
00381                                                         content,
00382                                                         fileLastWriteTime);
00383 
00384                                                     editContext.TextEditorService.ModelApi.ApplySyntaxHighlighting(
00385                                                         editContext,
00386                                                         modelModifier);
00387                                                     return Task.CompletedTask;
00388                                                 });
00389                                         });
00390                                 return Task.CompletedTask;
00391                             })
00392                         },
00393                         {
00394                             nameof(IBooleanPromptOrCancelRendererType.OnAfterDeclineFunc),
00395                             new Func<Task>(() =>
00396                             {
00397                                 dispatcher.Dispatch(new NotificationState.DisposeAction(
00398                                     notificationInformativeKey));
00399 
00400                                 return Task.CompletedTask;
00401                             })
00402                         },
00403                 },
00404                 TimeSpan.FromSeconds(20),
00405                 true,
00406                 null);
00407 
00408             dispatcher.Dispatch(new NotificationState.RegisterAction(
00409                 notificationInformative));
00410         }
00411     }
