00106     protected override async Task OnAfterRenderAsync(bool firstRender)
00107     {
00108         var renderBatchLocal = RenderBatch;
00109         if (renderBatchLocal is null)
00110             return;
00111             
00112         if (_lastSeenShowFindOverlayValue != renderBatchLocal.ViewModel.ShowFindOverlay)
00113         {
00114             _lastSeenShowFindOverlayValue = renderBatchLocal.ViewModel.ShowFindOverlay;
00115 
00116             // If it changes from 'false' to 'true', focus the input element
00117             if (_lastSeenShowFindOverlayValue)
00118             {
00119                 await JsRuntime.GetLuthetusCommonApi()
00120                     .FocusHtmlElementById(renderBatchLocal.ViewModel.FindOverlayId)
00121                     .ConfigureAwait(false);
00122             }
00123         }
00124 
00125         await base.OnAfterRenderAsync(firstRender);
00126     }
