00128     private void HandleOnFocus()
00129     {
00130         var renderBatchLocal = RenderBatch;
00131         if (renderBatchLocal is null)
00132             return;
00133         
00134         // In the case where the find over value was changed, by an outside event,
00135         // just refresh the InputValue to be sure its up to date.
00136         //
00137         // Example: user has a selection when using the keybind to open the find overlay,
00138         //       then the find overlay would be populated with their text selection.
00139         InputValue = renderBatchLocal.ViewModel.FindOverlayValue;
00140     }
